	private MPanel mainPanel;
	private MSplitPane mainSplitPane;
	private MToolBar editorToolBar;
	private MToolBar mainToolBar;
	private Object updateComponentsLock = new Object();
	private Object updateStateLock = new Object();
			private PreviousAction previousAction;
	private NextAction nextAction;
	private PrintAction printAction;
		private FindAction findAction;
	private FindNextAction findNextAction;
		private AppAction fullAction;
	private AppAction minimalAction;
	private AppAction horizontalSplitAction;
