	private ToggleDesignerAction toggleDesignerAction;
	private ToggleDesktopAction toggleDesktopAction;
	private ShowHideSidebarAction showHideSidebarAction;
	private ShowHideMainToolBarAction showHideMainToolBarAction;
	private ShowHideEditorToolBarAction showHideEditorToolBarAction;
	private ShowHideStatusBarAction showHideStatusBarAction;
	private PresentationAction presentationAction;
	private ZoomAction zoomInAction;
	private ZoomAction zoomOutAction;
		private InternetSearchAction internetSearchAction;
	private SearchAction searchAction;
		private HelpAction helpAction;
		private LockAction lockAction;
	public Editor getEditor() {
		return Editor.getCurrentEditor();
	}
	public Object getElementById(final String id) {
		Elements elements = new Elements();
				elements.put("file-menu", fileMenu);
			elements.put("tree-menu", treeMenu);
			elements.put("tabs-menu", tabsMenu);
			elements.put("import-menu", importMenu);
			elements.put("export-menu", exportMenu);
		elements.put("edit-menu", editMenu);
		elements.put("view-menu", viewMenu);
			elements.put("view-mode-menu", viewModeMenu);
			elements.put("goto-menu", goToMenu);
		elements.put("bookmarks-menu", bookmarksMenu);
		elements.put("tags-menu", tagsMenu);
		elements.put("tools-menu", toolsMenu);
		elements.put("help-menu", helpMenu);
				elements.put("editor-tool-bar", editorToolBar);
		elements.put("main-tool-bar", mainToolBar);
				elements.put("main-split-pane", mainSplitPane);
				elements.put("previous-action", previousAction);
		elements.put("next-action", nextAction);
		elements.put("presentation-action", presentationAction);
		return elements.get(id);
	}
	public synchronized static MainWindow getInstance() {
		if (_instance == null)
			new MainWindow(); 
		return _instance;
	}
	public void help() {
		helpAction.fire();
	}
	public void initEngine(final ScriptEngine engine) {
		engine.put("window", this);
		engine.put("sidebar", Sidebar.getInstance());
		engine.put("tabs", Tabs.getInstance());
		engine.put("tree", Tree.getInstance());
		engine.put("Utils", ScriptUtils.getInstance());
	}
	public boolean isDesktop() {
		return DesktopPanel.getInstance().isVisible();
	}
	public void setDesktop(final boolean value) {
		desktop = value;
		mainPanel.showCard(desktop ? "Desktop" : "Editor");
		toggleDesktopAction.setEnabled(Vars.desktopEnabled.get() && !Tabs.getInstance().isEmpty());
		updateState();
		if (desktop)
			DesktopPanel.getInstance().focus();
	}
	public boolean isHorizontalSplit() {
		return mainSplitPane.getOrientation() == MSplitPane.HORIZONTAL_SPLIT;
	}
	public static boolean isInstance() {
		return _instance != null;
	}
	@Override
	public Object onDynamicInit(final Object object) {
		if (object == null)
			return null;
		synchronized (object) {
			if (object instanceof MAbout) {
				MAbout about = MAbout.class.cast(object);
				about.addCredit("Amarok Team - Rediscover Your Music!", "Blend Algorithm", "http:				about.addCredit("Everaldo Coelho and KDE Team", "Crystal SVG Icon Theme", "http:				about.addCredit("Jerry Huxtable", "Image Filters", "http:				about.addCredit("M. Korbar", "German Translation, Ideas, Testing", "gmx\u002enet", "lopoff");
				about.addCredit("Romain Guy", "Java/Swing Special Effects", "http:				about.addCredit("Santhosh Kumar", "Java/Swing Ideas", "http:				about.addCredit("Scott Violet", "Ocean Gradient Customizer", "http:				about.addCredit("Stephen Kelvin Friedrich", "DataTips - Data Popups for JTree, JTable, JList", "http:				about.addCredit("SourceForge.net", "Project Hosting", "http:				about.addCredit("Yellowicon Studio", "\"Emotion\" Icon Set", "http:			}
			else if (object instanceof MouseGestures) {
				final Tabs tabs = Tabs.getInstance();
				final TreePanel treePanel = TreePanel.getInstance();
				MouseGestures mouseGestures = MouseGestures.class.cast(object);
				mouseGestures.registerGesture("D", treePanel.getNewFileAction());
				mouseGestures.registerGesture("Dcs", Application.getQuitAction());
				mouseGestures.registerGesture("Dc", _("Minimal View"), minimalAction);
				mouseGestures.registerGesture("Ds", treePanel.getNewFolderAction());
				mouseGestures.registerGesture("DR", tabs.getCloseAction());
				mouseGestures.registerGesture("DRs", tabs.getCloseAllAction());
				mouseGestures.registerGesture("L", previousAction);
				mouseGestures.registerGesture("LD", searchAction);
				mouseGestures.registerGesture("LR", new AppAction(_("Show/Hide Sidebar")) {
					@Override
					public void onAction() {
						mainSplitPane.toggleSidebar();
					}
				} );
				mouseGestures.registerGesture("LU", new AppAction(_("Show Tree")) {
					@Override
					public void onAction() {
						Sidebar.getInstance().goTo(SidebarTab.TREE);
					}
				} );
				mouseGestures.registerGesture("R", nextAction);
				mouseGestures.registerGesture("U", internetSearchAction);
				mouseGestures.registerGesture("Uc", _("Full View"), fullAction);
				mouseGestures.registerGesture("UD", toggleDesktopAction);
				mouseGestures.registerGesture("UR", _("Find Text"), findAction);
				mouseGestures.registerGesture("URDL", printAction);
				mouseGestures.registerGesture("Us", presentationAction);
			}
		}
		return null;
	}
	@Override
	public Object onDynamicInit(final Object source, final Object object) {
		if (source instanceof TrayIcon) {
			if (object instanceof MMenu) {
				synchronized (object) {
					MMenu menu = (MMenu)object;
					menu.add(TreePanel.getInstance().getQuickPasteAction());
					updateMenu(PluginManager.getInfos(), menu, PluginMenu.TRAY_MENU, false);
				}
			}
			else if (object instanceof MouseEvent) {
				synchronized (object) {
					MouseEvent event = (MouseEvent)object;
					if (MMouseAdapter.isMiddle(event)) {
						ImportExport.importClipboard();
						return true;
					}
				}
			}
		}
		return null;
	}
	public synchronized void saveSession(final int flags) {
		MLogger.info("makagiga", "Saving session...");
		MCookieManager.save();
		Config config = Config.getDefault();
				config.write("Splitter.Main.location", mainSplitPane.getDividerLocation());
		config.write("Splitter.Main.orientation", isHorizontalSplit() ? "HORIZONTAL" : "VERTICAL");
				MetaInfo metaInfo = Tabs.getInstance().getCurrentMetaInfo();
		config.write("Tabs.current", (metaInfo == null) ? "" : metaInfo.getFilePath());
				config.write("StatusBar.enabled", showHideStatusBarAction.isSelected());
		config.write("ToolBar.Editor.enabled", showHideEditorToolBarAction.isSelected());
		config.write("ToolBar.Main.enabled", showHideMainToolBarAction.isSelected());
				config.write("Window.width", getWidth());
		config.write("Window.height", getHeight());
		config.write("Window.x", getX());
		config.write("Window.y", getY());
		Tabs.getInstance().saveAllEditors(flags);
		Tree.getInstance().saveAllItems();
				DesktopPanel.getInstance().saveSession();
		config.save();
	}
	public void setEditorStatusText(final String value) {
		if (TK.isEmpty(value)) {
			Editor<?> editor = Tabs.getInstance().getSelectedTab();
			String text = null;
			if (editor != null) {
				if (editor.getInfo() == null) {
					AbstractFS fs = editor.getMetaInfo().getFS();
					text = (fs == null) ? null : fs.getName();
				}
				else {
					text = editor.getInfo().toString();
				}
			}
			editorStatusText.setText(text);
			editorStatusText.setToolTipText(text);
			editorStatusText.setVisible(text != null);
		}
		else {
			editorStatusText.setText(value);
			editorStatusText.setToolTipText(value);
			editorStatusText.setVisible(true);
		}
	}
	public static void showSidebar() {
		getInstance().mainSplitPane.setSidebarVisible(true);
	}
	public void uncaughtException(final Thread t, final Throwable e) {
		MLogger.exception(e);
		if (!Application.isInitialized() && !wasFatal) {
			wasFatal = true;
			MMessage.error(null, e, UI.makeHTML(_("A fatal error occured during Makagiga startup.<br>See <code>{0}</code> file for details.<br><br>Please try to run the application again.", Console.IO.getLog().getFile())));
			dispose();
						Config config = Config.getDefault();
			config.write("safeMode", true);
			config.save();
			System.exit(1);
		}
				if (
			(e instanceof ScriptException) ||
			e.getClass().getName().startsWith("sun.org.mozilla.javascript.")
		) {
			ScriptError.getSharedInstance().showError(e);
			return;
		}
		else if (
			(e.getCause() instanceof PrivilegedActionException) ||
			(e.getCause() instanceof ScriptException)
		) {
			ScriptError.getSharedInstance().showError(e.getCause());
			return;
		}
		if (!(e instanceof OutOfMemoryError) && MLogger.developer.get()) {
			MSplashScreen.close();
			Console.getInstance(true);
		}
	}
	public void updateComponents(final boolean updateUI, final MetaInfo... metaInfo) {
		synchronized (updateComponentsLock) { 			final Tabs tabs = Tabs.getInstance();
			if (metaInfo != null) {
				Editor<?> currentTab = tabs.getSelectedTab();
				Editor<?> editor;
				for (MetaInfo i : metaInfo) {
					if (i.isFile() || i.isVirtualFile()) {
						tabs.setTabInfo(i);
						editor = tabs.getTabAt(tabs.findEditor(i));
						if (editor != null) {
							boolean readOnly = !i.canModify();
							editor.setReadOnly(readOnly);
							if (editor instanceof EditorDesigner)
								EditorDesigner.class.cast(editor).getEditorDesigner().setReadOnly(readOnly);
							editor.updateActions();
						}
					}
										if ((currentTab != null) && (lockAction != null) && (currentTab.getMetaInfo() == i))
						lockAction.setMetaInfo(i);
				}
								if (currentTab != null)
					LocationBar.getInstance().update(currentTab.getMetaInfo());
			}
			updateTitle();
			if (updateUI)
				Tree.getInstance().updateUI();
			TreePanel.getInstance().updateActions();
			tabs.repaint();
		}
	}
	public static void updateMenu(final PluginManager.Table plugins, final MMenu menu, final String type, final boolean addSeparator) {
		int count = menu.getItemCount();
		Pluggable p;
		for (PluginInfo i : plugins) {
			p = i.getPluggable();
			if (p instanceof PluginMenu)
				PluginMenu.class.cast(p).updateMenu(type, menu);
		}
		if (addSeparator && (menu.getItemCount() != count))
			menu.addSeparator();
	}
	public static void updateMenu(final PluginMenu plugin, final MMenu menu, final String type, final boolean addSeparator) {
		int count = menu.getItemCount();
		plugin.updateMenu(type, menu);
		if (addSeparator && (menu.getItemCount() != count))
			menu.addSeparator();
	}
	public static void updateToolBar(final PluginManager.Table plugins, final MToolBar toolBar, final String type, final boolean addSeparator) {
		boolean editorToolBar = type.equals(PluginMenu.EDITOR_TOOL_BAR);
		int count = toolBar.getComponentCount();
		Pluggable p;
		for (PluginInfo i : plugins) {
			p = i.getPluggable();
						if (editorToolBar && (p instanceof EditorPlugin))
				continue; 			
			if (p instanceof PluginMenu)
				PluginMenu.class.cast(p).updateToolBar(type, toolBar);
		}
		if (addSeparator && (toolBar.getComponentCount() != count))
			toolBar.addSeparator();
	}
	public void alert(final String text) {
		MMessage.info(this, text);
	}
	public boolean confirm(final String text) {
		return MMessage.confirm(this, text);
	}
	public String prompt(final String text, final String defaultText) {
		return MMessage.input(this, defaultText, text, AppInfo.fullName.get());
	}
	@Override
	protected void onMinimize() {
		saveSession(Tabs.NORMAL_SAVE);
	}
	private MainWindow() {
		_instance = this;
		Thread.currentThread().setDefaultUncaughtExceptionHandler(this);
		Size.setMinimum(this, DEFAULT_WIDTH / 2, DEFAULT_HEIGHT / 2);
		if (!MSplashScreen.isAvailable())
			MSplashScreen.setImage(new ImageIcon("splash.png").getImage());
		ScriptExecutor.setInitializer(this); 		initPlugins();
								final Tabs tabs = Tabs.getInstance();
		final Sidebar sidebar = Sidebar.getInstance();
		PluginManager.Table infos = PluginManager.getInfos();
		initActions();
		initMenuBar(infos);
		initStatusBar();
				mainSplitPane = new MSplitPane();
		mainSplitPane.setBorder(null);
		mainSplitPane.setSidebar(sidebar);
				editorPanel = MPanel.createBorderPanel();
		editorPanel.addCenter(tabs);
		editorPanel.setVisible(false); 		
				final TreePanel treePanel = TreePanel.getInstance();
		mainToolBar = new MToolBar();
		mainToolBar.add(treePanel.getNewFileAction(), MToolBar.SHOW_TEXT);
		mainToolBar.add(treePanel.getQuickPasteAction());
		mainToolBar.add(treePanel.getNewFolderAction());
		treePanel.setLabelMenuButton(mainToolBar.add(treePanel.getLabelMenuAction()));
		mainToolBar.add(internetSearchAction);
		updateToolBar(infos, mainToolBar, PluginMenu.MAIN_TOOL_BAR, true);
		mainToolBar.addStretch();
		if (Vars.desktopEnabled.get())
			DesktopToolBar.getInstance().updateToolBar(mainToolBar, toggleDesktopAction);
		mainToolBar.add(previousAction);
		mainToolBar.add(nextAction, MToolBar.SHOW_TEXT);
		addNorth(mainToolBar);
				mainPanel = MPanel.createCardPanel();
		mainPanel.add(editorPanel, "Editor");
		mainPanel.add(DesktopPanel.getInstance(), "Desktop");
		mainSplitPane.setRightComponent(mainPanel);
		addCenter(mainSplitPane);
						presentationAction.connect(mainPanel, JComponent.WHEN_IN_FOCUSED_WINDOW);
		MSplashScreen.close();
		MNotification.Message message = new MNotification.Message(AppInfo.fullName.get(), _("Restoring session..."), AppInfo.icon.get());
		message.setCloseButtonVisible(false);
		message.setCountInfoVisible(false);
		MNotification.showMessage(message);
		restoreSession();
		MNotification.getInstance().hideMessage(message);
				ScriptException userScriptError = null;
		if (ScriptExecutor.userScript.get()) {
			File userScriptFile = new File(FS.makePath(FS.makeConfigPath("scripts"), "user.js"));
			if (userScriptFile.exists()) {
				try {
					ScriptExecutor.execute(userScriptFile);
				}
				catch (FileNotFoundException exception) { } 				catch (ScriptException exception) {
					userScriptError = exception;
				}
				catch (Exception exception) {
					MLogger.exception(exception);
				}
			}
		}
		final boolean autostart = Args.isSet("autostart");
		if (!autostart) {
			setVisible(true);
			toFront();
		}
		if (tabs.isEmpty())
			Tree.getInstance().requestFocusInWindow();
		else
						tabs.selectEditorAt(tabs.findEditor(Config.getDefault().read("Tabs.current", null)));
		MStatusBar.ready();
		setDesktop(tabs.isEmpty());
				tabs.addChangeListener(new ChangeListener() {
			public void stateChanged(final ChangeEvent e) {
				setDesktop(tabs.isEmpty());
			}
		} );
		initAutosave();
		setMouseGestures(new MouseGestures());
		if (!(OS.isKDE() && Vars.hackKDETrayIcon.get()))
			MSystemTray.setVisible(UI.systemTray.get());
		if (autostart && (MSystemTray.getIcon() == null)) {
			setState(ICONIFIED);
			setVisible(true);
			toFront();
		}
				if (Args.isSet("time-demo")) {
			MLogger.info("makagiga", "END TIME DEMO");
			Application.quit();
		}
		InternetSearch.installGlobalMenu();
		MCookieManager.init();
		MAuthenticator.init();
		if (userScriptError != null)
			ScriptError.getSharedInstance().showError(userScriptError);
				infos = PluginManager.getInfos(); 		for (PluginInfo i : infos) {
			try {
				i.getPluggable().onPostInit();
			}
			catch (PluginException exception) {
				MLogger.exception(exception);
			}
		}
		Tracker.startScheduler();
		if (!OS.isSupported())
			MNotification.showWarning(_("This operating system ({0}) is not fully supported", OS.getName()));
			}
	private void initActions() {
				previousAction = new PreviousAction();
		nextAction = new NextAction();
		printAction = new PrintAction();
				findAction = new FindAction();
				toggleSummaryAction = new ToggleSummaryAction();
		toggleDesignerAction = new ToggleDesignerAction();
		toggleDesktopAction = new ToggleDesktopAction();
		showHideSidebarAction = new ShowHideSidebarAction();
		showHideMainToolBarAction = new ShowHideMainToolBarAction();
		showHideEditorToolBarAction = new ShowHideEditorToolBarAction();
		showHideStatusBarAction = new ShowHideStatusBarAction();
		presentationAction = new PresentationAction();
				internetSearchAction = new InternetSearchAction();
		searchAction = new SearchAction();
				helpAction = new HelpAction();
	}
	private void initAutosave() {
		new MTimer(MTimer.Format.MINUTES, 10) {
			@Override
			protected void onTimeout() {
				if (!Vars.autosaveEnabled.get())
					return;
				try {
					stop(); 					String message = _("Autosave...");
					MStatusBar.setInfo(message);
					MLogger.info("makagiga", message);
					saveSession(Tabs.QUIET_SAVE);
				}
				finally {
					restart();
					MStatusBar.ready();
				}
			}
		}.start();
	}
	private void initExportMenu() {
		exportMenu.removeAll();
		exportMenu.setSplitMenu(true);
		Editor<?> editor = Tabs.getInstance().getSelectedTab();
		if ((editor != null) && (editor.getInfo() != null)) {
			exportMenu.addTitle(_("Export \"{0}\"", editor.getMetaInfo()));
						EditorPlugin.FileType[] types = EditorPlugin.fromInfo(editor.getInfo()).exportTypes.get();
			if (types.length > 0) {
				for (EditorPlugin.FileType i : types)
					exportMenu.add(new ImportExport.ExportAction(i));
				exportMenu.addSeparator();
			}
			exportMenu.add(new ImportExport.ExportAction(new EditorPlugin.FileType("png", _("PNG Image"), "image/png", true)));
		}
		MetaInfo currentFolder = Tree.getInstance().getCurrentFolder();
		AbstractFS currentFS = currentFolder.getFS();
		if (currentFS instanceof PluginMenu)
			PluginMenu.class.cast(currentFS).updateMenu(PluginMenu.EXPORT_MENU, exportMenu);
		if (exportMenu.isEmpty())
			exportMenu.addTitle(_("Select an item you want to export"));
	}
	private void initImportMenu() {
		importMenu.removeAll();
		importMenu.setSplitMenu(true);
		MetaInfo currentFolder = Tree.getInstance().getCurrentFolder();
		AbstractFS currentFS = currentFolder.getFS();
		if ((currentFS instanceof TreeFS) && currentFolder.canModify()) {
			importMenu.addTitle(_("Import To \"{0}\"", currentFolder));
			MMenu importFromMenu = new MMenu(_("Import From..."));
			importFromMenu.add(new AppAction("Incollector") {
				@Override
				public void onAction() {
					ImportExport.importIncollector();
				}
			} );
			importMenu.add(importFromMenu);
			importMenu.addSeparator();
			EditorPlugin plugin;
			EditorPlugin.FileType[] types;
			String text;
			for (PluginInfo i : PluginManager.getInfos(Pluggable.EDITOR)) {
				plugin = EditorPlugin.fromInfo(i);
				types = plugin.importTypes.get();
				if (types.length > 0) {
					AppAction importAction = new ImportExport.ImportAction(plugin, i.toString(), types);
					importAction.setIcon(i.getIcon());
					importMenu.add(importAction);
				}
			}
		}
		if (currentFS instanceof PluginMenu)
			PluginMenu.class.cast(currentFS).updateMenu(PluginMenu.IMPORT_MENU, importMenu);
		if (importMenu.isEmpty())
			importMenu.addTitle(_("No folder selected or import is not supported"));
	}
	private void initMenuBar(final PluginManager.Table plugins) {
		final Tabs tabs = Tabs.getInstance();
		MMenuBar menuBar = new MMenuBar();
		menuBar.getSelectionModel().addChangeListener(new ChangeListener() {
			public void stateChanged(final ChangeEvent e) {
				LiveTutorial.setActive(false);
			}
		} );
				fileMenu = new MMenu(_("&File"));
		fileMenu.setSplitMenu(true);
		treeMenu = new MMenu(_("Tree"), "ui/detailedview");
		treeMenu.setToolTipText(_("This menu allows you to modify all the files and folders."));
		treeMenu.addMenuListener(new MMenuAdapter(true) {
			@Override
			protected void onSelect(final MMenu menu) {
				if (menu.isEmpty())
					TreePanel.getInstance().createTreeMenu(menu, true);
			}
		} );
		fileMenu.add(treeMenu);
		fileMenu.addSeparator();
		fileMenu.add(previousAction);
		fileMenu.add(nextAction);
		tabsMenu = tabs.createTabsMenu();
