	private void updateState() {
		synchronized (updateStateLock) { 			Designer designer = null;
			final Sidebar sidebar = Sidebar.getInstance();
			final Tabs tabs = Tabs.getInstance();
			editMenu.removeAll();
			viewMenu.removeFrom(viewMenuItemCount);
						tabs.updateState();
						AppAction closeAction = tabs.getCloseAction();
			tabsMenu.setEnabled(closeAction.isEnabled());
			MetaInfo metaInfo = tabs.getCurrentMetaInfo();
			Tree.getInstance().focusItem(metaInfo);
						Editor<?> editor = tabs.getSelectedTab();
			if ((editor != null) && !isDesktop()) {
								boolean hasNext = false;
				boolean hasPrevious = false;
				MetaInfo parentFolder = (MetaInfo)metaInfo.getParentItem();
				if (parentFolder != null) {
					MetaInfo[] files = parentFolder.getSubMetaInfos(true);
					if (files.length > 1) {
												boolean afterCurrent = false;
						for (int i = 0; i < files.length; i++) {
							if (afterCurrent && files[i].isAnyFile()) {
								hasNext = true;
								break; 							}
							if (files[i] == metaInfo)
								afterCurrent = true;
						}
												boolean beforeCurrent = false;
						for (int i = files.length - 1; i >= 0; --i) {
							if (beforeCurrent && files[i].isAnyFile()) {
								hasPrevious = true;
								break; 							}
							if (files[i] == metaInfo)
								beforeCurrent = true;
						}
					}
				}
				previousAction.setEnabled(hasPrevious);
				nextAction.setEnabled(hasNext);
								if (DesktopToolBar.isInstance())
					DesktopToolBar.getInstance().setVisible(false);
								final LocationBar locationBar = LocationBar.getInstance();
				locationBar.update(metaInfo);
				locationBar.setVisible(true);
								if (editor instanceof PluginMenu) {
					PluginMenu pluginMenu = (PluginMenu)editor;
					pluginMenu.updateMenu(PluginMenu.EDIT_MENU, editMenu);
				}
								MMenu.Title tabTitle = viewMenu.addTitle(_("Current Tab"));
				int tabTitleCount = viewMenu.getItemCount();
				if (editor instanceof EditorZoom) {
					EditorZoom editorZoom = (EditorZoom)editor;
					if (zoomInAction == null)
						zoomInAction = new ZoomAction(EditorZoom.ZoomType.IN);
					zoomInAction.setEnabled(editorZoom.isZoomEnabled(EditorZoom.ZoomType.IN));
					viewMenu.add(zoomInAction);
					if (zoomOutAction == null)
						zoomOutAction = new ZoomAction(EditorZoom.ZoomType.OUT);
					zoomOutAction.setEnabled(editorZoom.isZoomEnabled(EditorZoom.ZoomType.OUT));
					viewMenu.add(zoomOutAction);
				}
				if (editor instanceof PluginMenu)
					updateMenu((PluginMenu)editor, viewMenu, PluginMenu.VIEW_MENU_EDITOR, false);
								if (viewMenu.getItemCount() == tabTitleCount)
					tabTitle.setVisible(false);
								if (editor instanceof EditorDesigner) {
					designer = EditorDesigner.class.cast(editor).getEditorDesigner();
					EditorDesigner.class.cast(editor).updateEditorDesigner();
				}
								if (editorToolBar == null) {
					editorToolBar = new MToolBar();
					editorToolBar.setOpaque(false);
					editorPanel.addNorth(editorToolBar);
				}
				else {
					editorToolBar.removeAll();
				}
				if (editor instanceof EditorPrint) {
					printAction.setEnabled(true);
					editorToolBar.add(printAction);
					editorToolBar.addSeparator();
				}
				else {
					printAction.setEnabled(false);
				}
				if (editor instanceof PluginMenu) {
					PluginMenu pluginMenu = (PluginMenu)editor;
					pluginMenu.updateToolBar(PluginMenu.EDITOR_TOOL_BAR, editorToolBar);
				}
				updateToolBar(PluginManager.getInfos(), editorToolBar, PluginMenu.EDITOR_TOOL_BAR, false);
				editorToolBar.addStretch();
								if (editor instanceof EditorZoom) {
					editorToolBar.addGap();
					JComponent zoomComponent = EditorZoom.class.cast(editor).getZoomComponent();
					if (zoomComponent != null) {
						editorToolBar.add(zoomComponent);
						editorToolBar.addGap();
					}
					editorToolBar.add(zoomInAction);
					editorToolBar.add(zoomOutAction);
					editorToolBar.addSeparator();
				}
								if (lockAction == null)
					lockAction = new LockAction();
				lockAction.setMetaInfo(metaInfo);
				editorToolBar.add(lockAction).setRequestFocusEnabled(true);
				if (Presentation.isActive())
					editorToolBar.setVisible(false);
				else
					editorToolBar.setVisible(showHideEditorToolBarAction.isSelected());
				editorToolBar.revalidate();
				editorToolBar.repaint();
				editorStatusText.setVisible(true);
				setEditorStatusText(editor.getStatusText());
								editor.updateActions();
				if (editor instanceof EditorSearch) {
					editMenu.addTitle(_("Search"));
										findAction.setEnabled(true);
					editMenu.add(findAction);
										if (findNextAction == null)
						findNextAction = new FindNextAction();
					findNextAction.setEnabled(true);
					editMenu.add(findNextAction);
				}
				else {
										findAction.setEnabled(false);
										if (findNextAction != null)
						findNextAction.setEnabled(false);
				}
			}
			else {
								previousAction.setEnabled(false);
				nextAction.setEnabled(false);
								if (DesktopToolBar.isInstance()) {
					final DesktopToolBar desktopToolBar = DesktopToolBar.getInstance();
					desktopToolBar.setVisible(true);
				}
								LocationBar.getInstance().setVisible(false);
				editorStatusText.setVisible(false);
								findAction.setEnabled(false);
				if (findNextAction != null)
					findNextAction.setEnabled(false);
				printAction.setEnabled(false);
								setEditorStatusText(null);
			}
			editMenu.setEnabled(!editMenu.isEmpty());
						if (EditorQuickFind.isInstance() && editorPanel.isAncestorOf(EditorQuickFind.getInstance()))
				editorPanel.remove(EditorQuickFind.getInstance());
						sidebar.setDesigner(designer);
			if (designer == null) {
				if (sidebar.isDesignerSelected())
					sidebar.goTo(SidebarTab.TREE, false);
				toggleDesignerAction.setEnabled(false);
			}
			else {
				designer.repaint(); 				if (editor != null)
					designer.setReadOnly(!editor.getMetaInfo().canModify());
				toggleDesignerAction.setEnabled(true);
			}
			updateTitle();
		}
	}
