"package org.makagiga.editors;
import static org.makagiga.commons.UI._;
import java.awt.Component;
import java.awt.event.MouseEvent;
import java.io.File;
import java.io.IOException;
import java.io.InputStream;
import java.io.OutputStream;
import java.math.BigInteger;
import java.security.DigestInputStream;
import java.security.DigestOutputStream;
import java.security.MessageDigest;
import java.text.MessageFormat;
import java.util.Arrays;
import java.util.Formatter;
import java.util.HashMap;
import java.util.Locale;
import java.util.Map;
import javax.crypto.Cipher;
import javax.crypto.CipherInputStream;
import javax.crypto.CipherOutputStream;
import javax.crypto.SecretKey;
import javax.crypto.SecretKeyFactory;
import javax.crypto.spec.PBEKeySpec;
import javax.crypto.spec.PBEParameterSpec;
import org.makagiga.MainWindow;
import org.makagiga.commons.*;
import org.makagiga.fs.MetaInfo;
import org.makagiga.plugins.PluginInfo;
import org.makagiga.plugins.PluginMenu;
import org.makagiga.tree.version.VersionControl;
public abstract class Editor<C extends Component> extends MPanel implements Modifiable {
	private boolean asyncLoad;
	private boolean doShowLockInfo = true;
	private boolean exportMode;
	private boolean modified;
	private boolean printing;
	private static Map<String, Integer> ref = new HashMap<String, Integer>();
	protected C core;
	private boolean readOnly;
	private static Editor<?> currentEditor;
	private MetaInfo metaInfo;
	private PluginInfo info;
	private String oldStatusText = "";
	public Editor() {
				String key = getClass().getName();
		synchronized (Editor.class) {
			Integer value = ref.get(key);
			ref.put(key, (value == null) ? 1 : (value + 1));
		}
	}
	public final void destroy() {
		onClose();
				String key = getClass().getName();
		synchronized (Editor.class) {
			Integer value = ref.get(key);
			if (value == 1) {
				ref.remove(key);
				onLastClose();
			}
			else {
				ref.put(key, value - 1);
			}
		}
	}
	public final void exportNotSupported(final EditorPlugin.FileType type) throws Exception {
		throw new Exception(_("Export to \"{0}\" is not supported", type));
	}
	public final C getCore() { return core; }
	public final void setCore(final C value) { core = value; }
	public synchronized static final Editor<?> getCurrentEditor() { return currentEditor; }
	public synchronized static final void setCurrentEditor(final Editor<?> value) { currentEditor = value; }
	public final PluginInfo getInfo() { return info; }
	public final void setInfo(final PluginInfo value) { info = value; }
	public final MetaInfo getMetaInfo() { return metaInfo; }
	public final void setMetaInfo(final MetaInfo value) { metaInfo = value; }
	public boolean isAsyncLoad() { return asyncLoad; }
	public void setAsyncLoad(final boolean value) { asyncLoad = value; }
	@Deprecated
	public final boolean isAutosave() { return false; }
	@Deprecated
	public final void setAutosave(final boolean value) { }
	public boolean isExportMode() { return exportMode; }
	public void setExportMode(final boolean value) { exportMode = value; }
	public boolean isPrinting() { return printing; }
	public void setPrinting(final boolean value) { printing = value; }
	public boolean isReadOnly() { return readOnly; }
	public void setReadOnly(final boolean value) {
		doShowLockInfo = value;
		readOnly = value;
	}
	public final void loadFromFile() throws Exception {
		if (this instanceof EditorIO) {
			File file = metaInfo.getFile();
			boolean newFile = file.length() == 0;
			InputStream input = null;
			try {
				Config config = metaInfo.getConfig();
				input = createInputStream(file, config);
				EditorIO.class.cast(this).loadFile(input, newFile);
				if (input instanceof DigestInputStream) {
					MessageDigest digest = DigestInputStream.class.cast(input).getMessageDigest();
					checkPassword(digest.digest(), config);
				}
			}
			finally {
				if (!asyncLoad)
					FS.close(input);
			}
		}
	}
	public final void saveToFile() throws Exception {
		if (this instanceof EditorIO) {
			OutputStream output = null;
			try {
				File outputFile = metaInfo.getFile();
								long freeSpace = outputFile.getFreeSpace();
				if ((freeSpace != 0) && (freeSpace < 16384))
					throw new IOException(String.format("No free disk space to save \"%s\"", outputFile));
								VersionControl.create(metaInfo, null, VersionControl.PREVIOUS);
				Config config = metaInfo.getConfig();
				output = createOutputStream(outputFile, config);
				EditorIO.class.cast(this).saveFile(output);
				if (output instanceof DigestOutputStream) {
					MessageDigest digest = DigestOutputStream.class.cast(output).getMessageDigest();
					config.write("cipher.messageDigest.algorithm", digest.getAlgorithm());
					config.write("cipher.messageDigest.value", formatDigest(digest.digest()));
					config.save();
				}
			}
			finally {
				FS.close(output);
			}
		}
	}
	public void updateActions() { }
	public final String getConfigEntry(final String configKey) {
		PluginInfo i =
			(info == null)
			? metaInfo.getFS().getInfo()
			: info;
		return configKey + "." + i.id;
	}
	public synchronized static final Component getCurrentCore() {
		return (currentEditor == null) ? null : currentEditor.getCore();
	}
	public synchronized final String getStatusText() { return oldStatusText; }
	public synchronized final void setStatusText(final String value) {
		oldStatusText = value;
		if (currentEditor == this) {
			if (MainWindow.isInstance())
				MainWindow.getInstance().setEditorStatusText(value);
			else
				MStatusBar.setText(value);
		}
	}
	public synchronized final boolean isCurrent() {
		return currentEditor == this;
	}
	public final void setStatusText(final String value, final Object... args) {
		setStatusText(MessageFormat.format(value, args));
	}
	public synchronized final void showLockInfo() {
		if (readOnly && doShowLockInfo) {
			doShowLockInfo = false;
			MStatusBar.setInfo(_("This file is locked. Click the \"Unlock\" button if you want to edit this file."));
		}
	}
	public synchronized final void syncConfig() {
		if (this instanceof EditorConfig) {
			Config global = Config.getDefault();
			Config local = metaInfo.getConfig();
			EditorConfig.class.cast(this).saveConfig(global, local);
			global.save();
						if ((local != null) && metaInfo.isWriteable())
				local.save();
		}
	}
	public void updateStatusText() {
		setStatusText(modified ? _("Modified") : "");
	}
	public void onClose() { }
	public void onLastClose() { }
	public boolean isModified() { return modified; }
	public void setModified(final boolean value, final Object event) {
		modified = value;
		updateStatusText();
	}
	protected void showPopupMenu(final String type, final MouseEvent e) {
		if (this instanceof PluginMenu) {
			MMenu menu = new MMenu();
			PluginMenu.class.cast(this).updateMenu(type, menu);
			menu.showPopup(e);
		}
	}
	private Cipher createCipher(final int mode, final Config config) throws Exception {
		byte[] saltArray = null;
		char[] password = null;
		int iterationCount = 0;
		PBEParameterSpec paramSpec = null;
		SecretKey key = null;
		String transformation = null;
		if (mode == Cipher.DECRYPT_MODE) {
			transformation = config.read("cipher.transformation", null);
			if (TK.isEmpty(transformation))
				return null;
			iterationCount = config.readInt("cipher.iterationCount", -1);
			String salt = config.read("cipher.salt", null);
			password = MMessage.enterPassword(null, metaInfo.getNicePath());
			if (password == null)
				throw new Exception(_("Invalid password"));
			String[] saltValues = salt.split(",");
			saltArray = new byte[saltValues.length];
			for (int i = 0; i < saltArray.length; i++)
				saltArray[i] = Byte.parseByte(saltValues[i]);
		}
		else if (mode == Cipher.ENCRYPT_MODE) {
			transformation = config.read("cipher.transformation", null);
			if (TK.isEmpty(transformation))
				return null;
			key = metaInfo.getProperty("cipher.key", null);
			paramSpec = metaInfo.getProperty("cipher.paramSpec", null);
			if ((key == null) || (paramSpec == null)) {
				password = MMessage.newPassword(null, metaInfo.getNicePath());
				if (TK.isEmpty(password))
					return null;
				iterationCount = 1024;
				transformation = "PBEWithSHA1AndDESede";
								saltArray = new byte[] { (byte)0x1, (byte)0x2, (byte)0x3, (byte)0x1, (byte)0x2, (byte)0x3, (byte)0x1, (byte)0x2, (byte)0x3 };
				config.write("cipher.iterationCount", iterationCount);
				String salt = "";
				for (byte  i : saltArray) {
					if (!salt.isEmpty())
						salt += ",";
					salt += Byte.toString(i);
				}
				config.write("cipher.salt", salt);
				config.write("cipher.transformation", transformation);
			}
		}
		Cipher cipher = null;
		try {
			if ((key == null) || (paramSpec == null)) {
				paramSpec = new PBEParameterSpec(saltArray, iterationCount);
				PBEKeySpec keySpec = new PBEKeySpec(password);
				key = SecretKeyFactory.getInstance(transformation).generateSecret(keySpec);
			}
			if (mode == Cipher.DECRYPT_MODE) {
				metaInfo.setProperty("cipher.key", key);
				metaInfo.setProperty("cipher.paramSpec", paramSpec);
			}
			cipher = Cipher.getInstance(transformation);
			cipher.init(mode, key, paramSpec);
		}
		finally {
			if (password != null)
				Arrays.fill(password, '0');
			if (saltArray != null)
				Arrays.fill(saltArray, (byte)0);
		}
		return cipher;
	}
	private InputStream createInputStream(final File file, final Config config) throws Exception {
		Cipher cipher = createCipher(Cipher.DECRYPT_MODE, config);
		InputStream stream = new FS.BufferedFileInput(file);
		return (cipher == null) ? stream : new DigestInputStream(new CipherInputStream(stream, cipher), MessageDigest.getInstance(config.read("cipher.messageDigest.algorithm", "SHA1")));
	}
	private OutputStream createOutputStream(final File file, final Config config) throws Exception {
		Cipher cipher = createCipher(Cipher.ENCRYPT_MODE, config);
		OutputStream stream = new FS.BufferedFileOutput(file);
		return (cipher == null) ? stream : new DigestOutputStream(new CipherOutputStream(stream, cipher), MessageDigest.getInstance(config.read("cipher.messageDigest.algorithm", "SHA1")));
	}
	private void checkPassword(final byte[] digest, final Config config) throws Exception {
		if (!formatDigest(digest).equals(config.read("cipher.messageDigest.value", null)))
			throw new Exception(_("Invalid password"));
	}
		private String formatDigest(final byte[] digest) {
		return new Formatter(Locale.US).format("%0" + (digest.length * 2) + "x", new BigInteger(1, digest)).toString();
	}
}"

