						tabs.setTabInfo(i);
						editor = tabs.getTabAt(tabs.findEditor(i));
						if (editor != null) {
							boolean readOnly = !i.canModify();
							editor.setReadOnly(readOnly);
							if (editor instanceof EditorDesigner)
								EditorDesigner.class.cast(editor).getEditorDesigner().setReadOnly(readOnly);
							editor.updateActions();
						}
					}
										if ((currentTab != null) && (lockAction != null) && (currentTab.getMetaInfo() == i))
						lockAction.setMetaInfo(i);
				}
								if (currentTab != null)
					LocationBar.getInstance().update(currentTab.getMetaInfo());
			}
			updateTitle();
			if (updateUI)
				Tree.getInstance().updateUI();
			TreePanel.getInstance().updateActions();
			tabs.repaint();
		}
	}
	public static void updateMenu(final PluginManager.Table plugins, final MMenu menu, final String type, final boolean addSeparator) {
		int count = menu.getItemCount();
		Pluggable p;
		for (PluginInfo i : plugins) {
			p = i.getPluggable();
			if (p instanceof PluginMenu)
				PluginMenu.class.cast(p).updateMenu(type, menu);
		}
		if (addSeparator && (menu.getItemCount() != count))
			menu.addSeparator();
	}
	public static void updateMenu(final PluginMenu plugin, final MMenu menu, final String type, final boolean addSeparator) {
		int count = menu.getItemCount();
		plugin.updateMenu(type, menu);
		if (addSeparator && (menu.getItemCount() != count))
			menu.addSeparator();
	}
	public static void updateToolBar(final PluginManager.Table plugins, final MToolBar toolBar, final String type, final boolean addSeparator) {
		boolean editorToolBar = type.equals(PluginMenu.EDITOR_TOOL_BAR);
		int count = toolBar.getComponentCount();
		Pluggable p;
		for (PluginInfo i : plugins) {
			p = i.getPluggable();
						if (editorToolBar && (p instanceof EditorPlugin))
				continue; 			
			if (p instanceof PluginMenu)
				PluginMenu.class.cast(p).updateToolBar(type, toolBar);
		}
		if (addSeparator && (toolBar.getComponentCount() != count))
			toolBar.addSeparator();
	}
