	private void navigate(final boolean next) {
		final Tabs tabs = Tabs.getInstance();
		Editor<?> currentEditor = tabs.getSelectedTab();
		if (currentEditor == null)
			return;
		MetaInfo currentFile = currentEditor.getMetaInfo();
		MetaInfo[] files = MetaInfo.class.cast(currentFile.getParentItem()).getSubMetaInfos(true);
		if (files.length < 2)
			return;
		MetaInfo newFile = null;
				if (next) {
			boolean afterCurrent = false;
			for (int i = 0; i < files.length; i++) {
				if (afterCurrent && files[i].isAnyFile()) {
					newFile = files[i];
					break; 				}
				if (files[i] == currentFile)
					afterCurrent = true;
			}
		}
				else {
			boolean beforeCurrent = false;
			for (int i = files.length - 1; i >= 0; --i) {
				if (beforeCurrent && files[i].isAnyFile()) {
					newFile = files[i];
					break; 				}
				if (files[i] == currentFile)
					beforeCurrent = true;
			}
		}
		if (newFile != null) {
			int index = tabs.findEditor(currentFile);
						tabs.closeEditorAt(index, Tabs.REMOVE_TAB | Tabs.SAVE_TO_FILE);
						Tree.getInstance().open(newFile);
		}
	}
	private void restoreSession() {
		Config config = Config.getDefault();
		setSize(
			Math.max(MIN_WIDTH, config.readInt("Window.width", DEFAULT_WIDTH)),
			Math.max(MIN_HEIGHT, config.readInt("Window.height", DEFAULT_HEIGHT))
		);
		setLocationByPlatform(true); 		if (firstRun) {
			setLocationRelativeTo(null);
		}
		else {
			setLocation(
				config.readInt("Window.x", 0),
				config.readInt("Window.y", 0)
			);
		}
				int dividerLocation = config.readInt("Splitter.Main.location", 250);
		mainSplitPane.setDividerLocation(dividerLocation);
		mainSplitPane.setSidebarVisible((dividerLocation == -1) || mainSplitPane.isSidebarVisible());
		String orientation = config.read("Splitter.Main.orientation", "HORIZONTAL");
		if ("VERTICAL".equals(orientation))
			setHorizontalSplit(false);
				MStatusBar.getGlobal().setClosed(!config.read("StatusBar.enabled", false));
		showHideEditorToolBarAction.setSelected(config.read("ToolBar.Editor.enabled", true));
		showHideMainToolBarAction.setSelected(config.read("ToolBar.Main.enabled", true));
		mainToolBar.setVisible(showHideMainToolBarAction.isSelected());
				if (Application.isSafeMode()) {
			config.write("safeMode", false);
			config.save();
		}
				Tree.getInstance().restoreAll();
				DesktopPanel.getInstance().restoreSession();
	}
