	public static Image waitForImage(final Image image) {
		Component component = (MFrame.getGlobal() == null) ? DummyComponent.getSharedInstance() : MFrame.getGlobal();
		MediaTracker mediaTracker = new MediaTracker(component);
		mediaTracker.addImage(image, 0);
		try {
			mediaTracker.waitForID(0);
		}
		catch (InterruptedException exception) {
			MLogger.exception(exception);
		}
		return image;
	}
	@Uninstantiable private UI() { }
	private static int getLAFCode() {
		synchronized (UI.class) {
			if (lafCode == -1) {
				Object laf = UIManager.getLookAndFeel();
				String className = (laf == null) ? null : laf.getClass().getName();
				if ("org.jvnet.substance.SubstanceLookAndFeel".equals(className))
					lafCode = 2;
				else if (laf instanceof MetalLookAndFeel)
					lafCode = 1;
				else
					lafCode = 0;
			}
		}
		return lafCode;
	}
