"package org.makagiga.commons;
import static java.lang.System.err;
import java.beans.PropertyChangeEvent;
import java.beans.PropertyChangeListener;
import java.lang.management.ManagementFactory;
import java.lang.management.MemoryMXBean;
import java.util.HashMap;
import java.util.IllegalFormatException;
import java.util.Map;
import java.util.logging.Formatter;
import java.util.logging.Handler;
import java.util.logging.Level;
import java.util.logging.Logger;
import java.util.logging.LogRecord;
public final class MLogger extends Logger {
	@ConfigEntry("Debug.log")
	public static final PropertyAccess<String> debugLog = new StringProperty("+core").getSecure();
	@ConfigEntry("developer")
	public static final PropertyAccess<Boolean> developer = new BooleanProperty().getSecure();
	private static boolean logAll;
	private static Map<String, Boolean> logConfig = new HashMap<String, Boolean>();
	private static MLogger _instance;
	private static String lastDebugLog;
	static {
		synchronized (MLogger.class) {
			_instance = new MLogger();
		}
	}
	public MLogger(final String name) {
		super(name, null);
		addHandler(new MHandler());
		setLevel(Level.ALL);
	}
	public static MSmallButton createMemoryPanel() {
		final MSmallButton button = new MSmallButton() {
			@Override
			protected void onClick() {
				System.gc();
			}
		};
		button.setFocusable(false);
		button.setStyle("font-size: smaller");
		button.setText("GC");
		button.setToolTipText("Heap memory usage. Click to run Garbage Collector.");
		MTimer timer = new MTimer(MTimer.Format.SECONDS, 2) {
			private long max;
			private long oldUsed;
			@Override
			protected boolean onTimeout() {
				if (!MLogger.developer.get())
					return true;
				MemoryMXBean memory = ManagementFactory.getMemoryMXBean();
				long used = memory.getHeapMemoryUsage().getUsed();
				long diff = used - oldUsed;
				oldUsed = used;
				max = Math.max(used, max);
				button.setForeground((diff <= 0) ? MColor.DARK_GREEN : MColor.DARK_RED);
				button.setText(
					MFormat.toMB(used) +
					" / " +
					MFormat.toMB(max) +
					" [" + MFormat.toMB(diff) + "]"
				);
				return true;
			}
		};
		timer.start();
		developer.addPropertyChangeListener(new PropertyChangeListener() {
			public void propertyChange(final PropertyChangeEvent e) {
				if (PropertyAccess.VALUE_PROPERTY.equals(e.getPropertyName())) {
					button.setVisible(MLogger.developer.get());
				}
			}
		} );
		return button;
	}
	public void debug(final String text) {
		if (isDebug())
			info("[" + text + "]");
	}
	public void debugFormat(final String text, final Object... args) {
		if (isDebug())
			info("[" + format(text, args) + "]");
	}
	public static void debug(final String id, final String text) {
		if (isDebug(id))
			_instance.info(id + " [" + text + "]");
	}
	public static void debug(final String id, final String text, final Object... args) {
		if (isDebug(id))
			_instance.info(id + " [" + format(text, args) + "]");
	}
	public void error(final String text) {
		if (isDebug())
			severe(getName() + ": " + text);
	}
	public void errorFormat(final String text, final Object... args) {
		if (isDebug())
			severe(getName() + ": " + format(text, args));
	}
	public static void error(final String id, final String text) {
		if (isDebug(id))
			_instance.severe(id + ": " + text);
	}
	public static void error(final String id, final String text, final Object... args) {
		if (isDebug(id))
			_instance.severe(id + ": " + format(text, args));
	}
	public static void exception(final Throwable exception) {
		if (exception == null)
			return;
		error("exception", exception.toString());
		exception.printStackTrace(err);
	}
	public static String format(final String format, final Object... args) {
		if (format == null)
			return "null";
		try {
			return String.format(format, args);
		}
		catch (IllegalFormatException exception) {
			exception.printStackTrace(err);
			return format;
		}
	}
	@Override
	public void info(final String text) {
		super.info(getName() + ": " + text);
	}
	public void infoFormat(final String text, final Object... args) {
		if (isDebug())
			info(format(text, args));
	}
	public static void info(final String id, final String text) {
		if (isDebug(id))
			_instance.info(id + ": " + text);
	}
	public static void info(final String id, final String text, final Object... args) {
		if (isDebug(id))
			_instance.info(id + ": " + format(text, args));
	}
	public boolean isDebug() {
		return isDebug(getName());
	}
	public synchronized static boolean isDebug(final String id) {
		try {
			String dl = debugLog.get();
			if ((dl == null) || !dl.equals(lastDebugLog)) {
				lastDebugLog = dl;
				logAll = false;
				logConfig.clear();
				if (!TK.isEmpty(lastDebugLog)) {
					for (String i : TK.fastSplit(lastDebugLog, ',')) {
						if (i.length() > 1) {
							if (i.equals("+all")) {
								logAll = true;
							}
							else {
								switch (i.charAt(0)) {
									case '+':
										logConfig.put(i.substring(1), true);
										break;
									case '-':
										logConfig.put(i.substring(1), false);
										break;
									default:
										err.println("MAKAGIGA: Invalid logger configuration (org.makagiga.commons.MLogger.debugLog): " + i);
								}
							}
						}
						else {
							err.println("MAKAGIGA: Invalid logger configuration (org.makagiga.commons.MLogger.debugLog): " + i);
						}
					}
				}
			}
			Boolean enabled = logConfig.get(id);
			if (logAll || developer.get()) {
				if ((enabled != null) && !enabled)
					return false;
				return true;
			}
			else {
				return (enabled != null) && enabled;
			}
		}
		catch (SecurityException exception) {
			exception.printStackTrace();
			return false;
		}
	}
	public static void trace() {
		Thread.dumpStack();
	}
	public static void trace(final int maxElements) {
		StackTraceElement[] stackTrace = Thread.currentThread().getStackTrace();
		for (int i = 2; i < stackTrace.length; i++) {
			if (i - 2 >= maxElements)
				break;
			System.err.println("\tat " + stackTrace[i]);
		}
	}
	@Override
	public void warning(final String text) {
		if (isDebug())
			super.warning(getName() + ": " + text);
	}
	public void warningFormat(final String text, final Object... args) {
		if (isDebug())
			super.warning(getName() + ": " + format(text, args));
	}
	public static void warning(final String id, final String text) {
		if (isDebug(id))
			_instance.warning(id + ": " + text);
	}
	public static void warning(final String id, final String text, final Object... args) {
		if (isDebug(id))
			_instance.warning(id + ": " + format(text, args));
	}
	public static void XXX() {
		StackTraceElement[] stackTrace = Thread.currentThread().getStackTrace();
		if (stackTrace.length > 2)
			XXX("YOU ARE HERE: %s", stackTrace[2]);
		else
			XXX("YOU ARE HERE");
	}
	public static void XXX(final Object object) {
		if (object == null)
			_instance.finest("***: null");
		else
			_instance.finest("***: " + object.getClass().getName() + ": \"" + object + "\"");
	}
	public static void XXX(final String text) {
		_instance.finest("***: " + text);
	}
	public static void XXX(final String text, final Object... args) {
		XXX(format(text, args));
	}
	public static void XXXTrace() {
		XXX();
		trace();
	}
	public static void XXXTrace(final String text) {
		XXX(text);
		trace();
	}
	private MLogger() {
		this("log");
		setUseParentHandlers(false);
	}
	private static final class MFormatter extends Formatter {
		public String format(final LogRecord record) {
			String defaultFormat = record.getLevel() + ": " + record.getMessage();
			if (FS.isRestricted())
				return defaultFormat;
			if (developer.get()) {
				String dateTime = new MDate(record.getMillis())
					.format(MDate.DEFAULT_DATE_TIME_FORMAT);
				defaultFormat =
					String.format(
						"[%s #%s] ",
						dateTime,
						record.getSequenceNumber()
					) + defaultFormat;
			}
			return MApplication.getInternalName() + ": " + defaultFormat;
		}
	}
	private static final class MHandler extends Handler {
		private MFormatter formatter = new MFormatter();
		public void close() { }
		public void flush() { }
		public void publish(final LogRecord record) {
			err.println(formatter.format(record));
		}
	}
}"

