"package org.makagiga.tree;
import static org.makagiga.commons.UI._;
import javax.swing.event.DocumentEvent;
import org.makagiga.commons.*;
import org.makagiga.commons.autocompletion.AutoCompletion;
import org.makagiga.fs.FSNewFile;
import org.makagiga.fs.tree.TreeFS;
import org.makagiga.plugins.Pluggable;
import org.makagiga.plugins.PluginInfo;
import org.makagiga.plugins.PluginManager;
import org.makagiga.tags.TagsEditor;
public class NewFileDialog extends MDialog {
	private MLabeledTextField fileName;
	private static String lastNewFileName;
	private TagsEditor tagsEditor;
	public NewFileDialog(final FSNewFile fs, final MPanel customPanel) {
		this(fs, null, customPanel);
	}
	public NewFileDialog(final FSNewFile fs, final String aNewName, final MPanel customPanel) {
		super(null, fs.getNewFileActionText(), "ui/newfile", STANDARD_DIALOG | URL_BUTTON);
		getURLButton().setVisible(false);
		MPanel p = MPanel.createVBoxPanel();
		changeButton(getOKButton(), _("Create"));
				String newName;
		if (TK.isEmpty(aNewName))
			newName = (lastNewFileName == null) ? fs.getNewFileDefaultName() : lastNewFileName;
		else
			newName = aNewName;
		fileName = new MLabeledTextField(_("Name:"), newName) {
			@Override
			protected void onChange(final DocumentEvent e) {
				getOKButton().setEnabled(!fileName.isEmpty());
			}
		};
		fileName.setAutoCompletion("newfile");
		p.add(fileName);
				if (fs instanceof TreeFS) {
			AutoCompletion autoCompletion = fileName.getAutoCompletion();
			for (PluginInfo i : PluginManager.getInfos(Pluggable.EDITOR))
				autoCompletion.addItem(i.toString());
		}
				p.addGap();
				tagsEditor = new TagsEditor();
		p.add(tagsEditor);
		if (customPanel != null) {
			p.addGap();
			p.add(customPanel);
		}
		addCenter(p);
		Size.packFixed(this, 480);
		fileName.setDefault();
	}
	public String getName() {
		return fileName.getText();
	}
	public String getTags() {
		return tagsEditor.getTags().toString();
	}
	@Override
	protected boolean onAccept() {
		tagsEditor.saveAutoCompletion();
		lastNewFileName = fileName.getText(); 		fileName.saveAutoCompletion();
		return true;
	}
}"

