	}
	public static KeyStroke getFindPreviousKeyStroke() {
				if (OS.isMac())
			return KeyStroke.getKeyStroke(VK_G, getMenuMask() | SHIFT_MASK);
				return KeyStroke.getKeyStroke(VK_F3, SHIFT_MASK);
	}
