				if (icon instanceof MIcon) {
					String iconName = MIcon.class.cast(icon).getName();
					if (iconName != null)
						icon = MIcon.medium(iconName);
				}
				return icon;
			default: 				icon = getValue(action, LARGE_ICON_KEY, null);
				if (icon == null)
					icon = getValue(action, SMALL_ICON, null);
				return icon;
		}
