			Window window = UI.windowFor(c);
			if (window != null)
				return window;
			c = c.getParent();
			if (c instanceof JPopupMenu) {
				c = JPopupMenu.class.cast(c).getInvoker();
				if (c instanceof MMenu) {
					c = c.getParent();
					if (c instanceof JPopupMenu)
						c = JPopupMenu.class.cast(c).getInvoker();
				}
				if (c != null)
					result = UI.windowFor(c);
			}
		}
