	public static StringBuilder escapeXML(StringBuilder buf, final CharSequence value) {
		if (value == null)
			return buf;
		int len = value.length();
		if (len == 0)
			return buf;
		if (buf == null)
			buf = new StringBuilder(len * 2);
		for (int i = 0; i < len; i++) {
			char c = value.charAt(i);
			switch (c) {
				case '<':
					buf.append("&lt;");
					break;
				case '>':
					buf.append("&gt;");
					break;
				case '&':
					buf.append("&amp;");
					break;
				case '\'':
															buf.append("&#039;");
					break;
				case '\"':
					buf.append("&quot;");
					break;
				default:
					buf.append(c);
					break;
			}
		}
		return buf;
	}
