	public static Iterator<String> breakIterator(final BreakIterator it, final String text) {
		Objects.requireNonNull(it);
		it.setText(text);
		return new Iterator<String>() {
			private int start = it.first();
			private int end = it.next();
			@Override
			public boolean hasNext() {
				return end != BreakIterator.DONE;
			}
			@Override
			public String next() {
				String result = text.substring(start, end);
				start = end;
				end = it.next();
				return result;
			}
		};
	}
	public static String buildErrorMessage(final Throwable throwable) {
		Objects.requireNonNull(throwable);
		int level = 0;
		String lastCauseString = null;
		MStringBuilder s = new MStringBuilder();
		Throwable cause = throwable;
		do {
			String causeString = cause.toString();
						if (Objects.equals(lastCauseString, causeString))
				continue; 
			lastCauseString = causeString;
			if (!s.isEmpty())
				s.n().n();
			if (level > 0) {
				s.append(UI.BULLET);
				s.append(' ');
			}
			s.append(identifierToDisplayString(cause.getClass().getSimpleName()));
			String message = cause.getLocalizedMessage();
			if (!isEmpty(message)) {
				s.append(": ");
				s.append(message);
				s.n();
			}
			else {
				s.append(' ');
			}
			s.append('(')
			.append(cause.getClass().getName())
			.append(')');
			level++;
		} while ((cause = cause.getCause()) != null);
		return s.toString();
	}
