	public static String identifierToDisplayString(final String identifier) {
		if (identifier == null)
			return null;
		int len = identifier.length();
		if (len == 0)
			return identifier;
		boolean nextUpper = false;
		boolean previousLower = false;
		boolean previousUpper = false;
		int extra = 0;
		StringBuilder s = new StringBuilder(len + 10);
		for (int i = 0; i < len; i++) {
			char c = identifier.charAt(i);
						if (s.length() == 0) {
				s.append(Character.toUpperCase(c));
				nextUpper = false;
				previousLower = false;
				previousUpper = true;
			}
						else if ((c == '_') || (c == ' ') || (c == '.')) {
				s.append(' ');
				nextUpper = true;
				previousLower = false;
				previousUpper = false;
			}
			else {
								if (Character.isUpperCase(c)) {
										if (previousLower) {
						s.append(' ');
						extra++;
						previousLower = false;
						previousUpper = false;
					}
					else {
						previousUpper = true;
					}
					s.append(c);
					nextUpper = false;
				}
								else if (nextUpper) {
					s.append(Character.toUpperCase(c));
					nextUpper = false;
					previousLower = false;
					previousUpper = true;
				}
				else if (Character.isLowerCase(c)) {
										if (previousUpper) {
						int insertAt = (i + extra) - 1;
						if ((insertAt > 0) && (s.charAt(Math.max(0, insertAt - 1)) != ' ')) {
							s.insert(insertAt, ' ');
							extra++;
						}
						previousUpper = false;
					}
					s.append(c);
					nextUpper = false;
					previousLower = true;
				}
				else {
					s.append(Character.toLowerCase(c));
					nextUpper = false;
					previousLower = true;
					previousUpper = false;
				}
			}
		}
		return s.toString();
	}
