	public static Pattern newTextSearchPattern(final CharSequence text, final int options) {
		Objects.requireNonNull(text);
		int flags = ((options & TEXT_SEARCH_PATTERN_CASE_SENSITIVE) != 0)
			? Pattern.LITERAL
			: Pattern.LITERAL | Pattern.CASE_INSENSITIVE | Pattern.UNICODE_CASE;
		int len = text.length();
		if (len == 0)
			return Pattern.compile("", flags);
		StringBuilder s = new StringBuilder(len + len / 2);
		boolean forceLiteral = true;
		for (int i = 0; i < len; i++) {
			char c = text.charAt(i);
			if (Character.isLetterOrDigit(c) || (c == '%')) {
				s.append(c);
				forceLiteral = false;
			}
			else if (Character.isWhitespace(c)) {
				s.append("(.|\\s)");
				flags &= ~Pattern.LITERAL;
			}
			else {
				s.append('.');
				flags &= ~Pattern.LITERAL;
			}
		}
		if (forceLiteral)
			flags |= Pattern.LITERAL;
		if ((flags & Pattern.LITERAL) != 0)
			return Pattern.compile(text.toString(), flags);
		return Pattern.compile(s.toString(), flags);
	}
