					buf.append("&gt;");
					break;
				case '&':
					buf.append("&amp;");
					break;
				case '\'':
															buf.append("&#039;");
					break;
				case '\"':
					buf.append("&quot;");
					break;
				default:
					buf.append(c);
					break;
			}
		}
		return buf;
	}
