				for (K i : order) {
			V v = values.remove(i);
			if (v == null)
				MLogger.warning("core", "TK.order: \"%s\" key not found", i);
			else
				result.add(v);
		}
				for (V v : values.values())
			result.add(v);
		return result;
	}
	public static List<String> parseArguments(final String arguments) throws ParseException {
		if (arguments == null)
			return Collections.emptyList();
		int len = arguments.length();
		if (len == 0)
			return Collections.emptyList();
		boolean inEscape = false;
		boolean inQuote = false;
		List<String> result = new ArrayList<>();
		StringBuilder arg = new StringBuilder();
		for (int i = 0; i < len; i++) {
			char c = arguments.charAt(i);
			switch (c) {
								case ' ':
				case '\n':
				case '\t':
					if (inQuote) {
						arg.append(c);
					}
					else {
						if (arg.length() > 0) {
							result.add(arg.toString()); 							arg.setLength(0); 						}
					}
					break;
								case '\\':
					if (inQuote) {
						if (inEscape) {
							arg.append('\\');
							inEscape = false;
						}
						else {
							inEscape = true;
						}
					}
					else {
						arg.append(c);
					}
					break;
								case '"':
					if (inQuote) {
						if (inEscape) {
							arg.append('"');
							inEscape = false;
						}
						else {
							result.add(arg.toString()); 							arg.setLength(0); 							inQuote = false;
						}
					}
					else {
						inQuote = true;
					}
					break;
				default:
					if (inEscape) {
						switch (c) {
							case 'n':
								arg.append('\n');
								break;
							case 't':
								arg.append('\t');
								break;
							default:
								throwInvalidEscapeSequence(i);
						}
						inEscape = false;
					}
					else {
						arg.append(c);
					}
			}
						if (i == len - 1) {
				if (inEscape) {
					throwInvalidEscapeSequence(i + 1);
				}
				if (inQuote) {
					throwMissingQuoteCharacter(i + 1);
				}
				if (arg.length() > 0) {
					result.add(arg.toString()); 									}
			}
		}
		return result;
	}
