	public static String unescapeXML(final String value) {
		if (isEmpty(value))
			return value;
		StringBuilder s = new StringBuilder(value);
		fastReplace(s, "&#039;", "\'");
		fastReplace(s, "&apos;", "\'");
		fastReplace(s, "&quot;", "\"");
		fastReplace(s, "&lt;", "<");
		fastReplace(s, "&gt;", ">");
		fastReplace(s, "&amp;", "&");
		return s.toString();
	}
