	public static String fastReplace(final String s, final String from, final String to) {
		StringBuilder buf = new StringBuilder(s);
		if (fastReplace(buf, from, to))
			return buf.toString();
		return s;
	}
	public static boolean fastReplace(final StringBuilder buf, final char from, final char to) {
		if (from == to)
			return false;
		int len = buf.length();
		if (len == 0)
			return false;
		boolean changed = false;
		for (int i = 0; i < len; i++) {
			if (buf.charAt(i) == from) {
				buf.setCharAt(i, to);
				changed = true;
			}
		}
		return changed;
	}
	public static boolean fastReplace(final StringBuilder buf, final String from, final String to) {
		int fromLen = from.length();
		int toLen = to.length();
		boolean useReplace = (fromLen == toLen);
		boolean changed = false;
		int i = 0;
		while ((i = buf.indexOf(from, i)) != -1) {
			changed = true;
			if (useReplace) {
				buf.replace(i, i + toLen, to);
			}
			else {
				buf.delete(i, i + fromLen);
				buf.insert(i, to);
			}
			i += toLen;
		}
		return changed;
	}
	public static List<String> fastSplit(final String text, final char delimeter) {
		if (text == null)
			return Collections.emptyList();
		int len = text.length();
		if (len == 0)
			return Collections.emptyList();
		List<String> result = new ArrayList<>();
		int fromIndex = 0;
		int lastIndex = -1;
		while (true) {
			fromIndex = text.indexOf(delimeter, fromIndex);
			if (fromIndex == -1) { 				if (len == lastIndex + 1)
					return result;
				if (lastIndex == -1)
					result.add(text);
				else
					result.add(text.substring(lastIndex + 1, len));
				return result;
			}
			else {
				if (fromIndex == lastIndex + 1) { 					fromIndex++;
					lastIndex++;
					continue; 				}
				if (lastIndex == -1)
					result.add(text.substring(0, fromIndex)); 				else
					result.add(text.substring(lastIndex + 1, fromIndex));
			}
			lastIndex = fromIndex++;
		}
	}
	public static boolean find(final String string, final Pattern substring) {
		Objects.requireNonNull(substring);
		if (isEmpty(string))
