		Toolkit.getDefaultToolkit().beep();
	}
	public static String centerSqueeze(final String text) {
		return centerSqueeze(text, 50);
	}
	public static String centerSqueeze(final String text, final int maxLength) {
		if (text == null)
			return null;
		int max = Math.max(3, maxLength);
		if (text.length() <= max)
			return text;
		if ((max == 3) || (text.length() < 4))
			return "...";
		int mid = text.length() / 2;
		int midCut = (text.length() - max) / 2;
		return text.substring(0, mid - midCut - 2) + "..." + text.substring(mid + midCut + 2);
	}
	public static void checkNull(final Object object, final String name) {
		if (object == null) {
			if (name == null)
				throw new NullPointerException();
			throw new NullPointerException("\"" + name + "\" is \"null\"");
		}
	}
	public static boolean containsIgnoreCase(final String string, final String substring) {
		if ((string == null) || (substring == null))
			return false;
		return string.toUpperCase().indexOf(substring.toUpperCase()) != -1;
	}
	public static String escapePath(final String value) {
		String[] parts = value.replace("\\", "/").split("/");
		StringBuilder result = new StringBuilder(value.length() * 2);
		for (String i : parts)
			result.append('/').append(TK.escapeURL(i));
		return result.toString();
	}
	public static String escapeURL(final String value) {
		return escapeURL(value, 0);
	}
	public static String escapeURL(final String value, final int flags) {
		try {
			String result = URLEncoder.encode(value, "UTF-8");
			if ((flags & ESCAPE_FILE) != 0)
				result = result.replace("*", "%2A");
			if ((flags & ESCAPE_SPACE) != 0)
				result = result.replace("+", "%20");
			return result;
		}
		catch (UnsupportedEncodingException exception) {
			MLogger.exception(exception);
			return value;
		}
	}
	public static String escapeXML(final String value) {
		if (value == null)
			return null;
		int len = value.length();
		if (len == 0)
			return value;
		StringBuilder buf = new StringBuilder(len * 2);
		StringCharacterIterator i = new StringCharacterIterator(value);
		for (char c = i.first(); c != StringCharacterIterator.DONE; c = i.next()) {
			switch (c) {
				case '<':
					buf.append("&lt;");
					break;
				case '>':
					buf.append("&gt;");
					break;
				case '&':
					buf.append("&amp;");
					break;
				case '\'':
					buf.append("&#039;");
					break;
				case '\"':
					buf.append("&quot;");
					break;
				default:
					buf.append(c);
					break;
			}
		}
