public final class Tree extends MTree<MetaInfo, MTreeModel> {
	private DnDTransferHandler dndTransferHandler;
	private ItemRenderer itemRenderer;
	private Map<String, AbstractFS> fileSystems = new HashMap<String, AbstractFS>();
	private MetaInfo mainRoot;
	private static Tree _instance;
	@Override
	public void addItem(final MetaInfo parent, final MetaInfo item) {
		Tracker.add(item, TrackerEvent.ID.ITEM_ADDED);
		super.addItem(parent, item);
	}
	@Override
	public void deleteItem(final MetaInfo item) {
		Tracker.add(item, TrackerEvent.ID.ITEM_REMOVED);
		super.deleteItem(item);
	}
	public void focusItem(final MetaInfo metaInfo) {
		if (metaInfo == null)
			return;
		new Scanner() {
			public void processItem(final MetaInfo item) {
				if (item == metaInfo) {
					selectItem(item, true);
					stop();
				}
			}
		};
	}
	public MetaInfo getCurrentFolder() {
		return getCurrentFolder(false);
	}
	public MetaInfo getCurrentFolder(final boolean allowDynamicFolder) {
		MetaInfo[] metaInfos = getSelectedItems();
				if (metaInfos.length == 0)
			return TreeFS.getInstance().getRoot();
		MetaInfo metaInfo = metaInfos[0];
		if (metaInfo.isAnyFile()) {
			MetaInfo parent = metaInfo.getParentFolder();
			if (parent == null)
				return metaInfo.getFS().getRoot();
			if (parent.isDynamicFolder())
				return allowDynamicFolder ? parent : parent.getParentFolder();
			return parent;
		}
		if (allowDynamicFolder && metaInfo.isDynamicFolder())
			return metaInfo;
				return metaInfo;
	}
	public Map<String, AbstractFS> getFileSystems() { return fileSystems; }
	public AbstractFS getFS(final String id) {
		return (id == null) ? null : fileSystems.get(id);
	}
	public synchronized static Tree getInstance() {
		if (_instance == null)
			new Tree(); 
		return _instance;
	}
	public MetaInfo[] getSelectedItems() {
		TreePath[] selection = getSelectionPaths();
		if (TK.isEmpty(selection))
			return new MetaInfo[0];
		MetaInfo[] metaInfos = new MetaInfo[selection.length];
		for (int i = 0; i < metaInfos.length; i++)
			metaInfos[i] = (MetaInfo)selection[i].getLastPathComponent();
		return metaInfos;
	}
	@Override
	public Point getToolTipLocation(final MouseEvent e) {
		if (!UI.toolTips.get())
			return null;
		int row = getRowForLocation(e.getPoint());
		if (row == -1)
			return null;
		Point viewPosition = MScrollPane.getViewport(this).getViewPosition();
		int x = Sidebar.getInstance().getLocation().x + viewPosition.x + 10;
		Rectangle r = getRowBounds(row);
		int y = r.y + r.height * 2;
		return new Point(x, y);
	}
	@Override
	public String getToolTipText(final MouseEvent e) {
		if (!UI.toolTips.get())
			return null;
		MetaInfo metaInfo = getItemForLocation(e.getPoint());
		if (metaInfo != null)
			return metaInfo.isFSRoot() ? metaInfo.toString() : metaInfo.getToolTipText();
		return null;
	}
	public boolean open(final MetaInfo metaInfo) {
		return open(null, metaInfo);
	}
	public boolean open(final InputEvent e, final MetaInfo metaInfo) {
		if (metaInfo == null)
			return false;
		if (metaInfo.isAnyFolder()) {
			Sidebar.getInstance().goTo(SidebarTab.TREE, false);
			if (UI.singleClickOpen.get()) {
				if (metaInfo.isLink())
					focusItem(metaInfo.getLink());
				else
					focusItem(metaInfo);
			}
			if (metaInfo.isDynamicFolder()) {
				if (metaInfo.isLink()) {
					MetaInfo link = metaInfo.getLink();
					if (link.getChildCount() == 0)
						return dynamicOpen(e, link);
					expandItem(link);
				}
				else {
					return dynamicOpen(e, metaInfo);
				}
			}
			return true;
		}
		if (metaInfo.isFile())
			return Tabs.getInstance().openEditor(metaInfo);
		if (metaInfo.isVirtualFile()) {
			focusItem(metaInfo);
			if (metaInfo.getFS() instanceof FSOpen) {
				try {
					int flags = 0;
					if (e instanceof MouseEvent) {
						MouseEvent me = (MouseEvent)e;
						if (MMouseAdapter.isLeft(me))
							flags = FSOpen.LEFT_BUTTON;
						else if (MMouseAdapter.isMiddle(me))
							flags = FSOpen.MIDDLE_BUTTON;
					}
					FSOpen.class.cast(metaInfo.getFS()).open(flags, metaInfo);
					return true;
				}
				catch (FSException exception) {
					MLogger.exception(exception);
					MStatusBar.setError(exception.getMessage());
				}
			}
		}
		return false;
	}
	public void removeAllChildren(final MetaInfo parent) {
		removeAllChildren(parent, true);
	}
	public synchronized void removeAllChildren(final MetaInfo parent, final boolean saveMetaInfo) {
				final Tabs tabs = Tabs.getInstance();
		new Scanner(parent) {
			public void processItem(final MetaInfo item) {
				Tracker.add(item, TrackerEvent.ID.ITEM_REMOVED);
				if (item.isAnyFile()) {
					int index = tabs.findEditor(item);
					if (index != -1) {
						tabs.closeEditorAt(index, Tabs.REMOVE_TAB | Tabs.SAVE_TO_FILE);
						if (saveMetaInfo)
							item.saveToFile();
					}
				}
			}
		};
		parent.removeAllChildren();
	}
	public void restoreAll() {
		MetaInfo root;
		for (final AbstractFS fs : fileSystems.values()) {
			root = fs.getRoot();
			new Scanner(root) {
				public void processItem(final MetaInfo metaInfo) {
					if (fs instanceof TreeFS) {
						if (metaInfo.isFile() && metaInfo.isOpen() && metaInfo.isWriteable()) {
							if (Application.isSafeMode()) {
								metaInfo.setOpen(false);
								metaInfo.saveToFile();
							}
							else {
																if (Vars.treeReopenFiles.get())
									Tabs.getInstance().openEditor(metaInfo);
								else
									metaInfo.setOpen(false);
							}
						}
					}
				}
			};
						if (root.isExpanded())
				expandItem(root);
		}
				if (getSelectedItems().length == 0)
			selectItem(TreeFS.getInstance().getRoot(), false);
	}
	public void saveAllItems() {
		MetaInfo root;
		for (final AbstractFS fs : fileSystems.values()) {
			root = fs.getRoot();
			root.setExpanded(isExpanded(root.getTreePath()));
			new Scanner(root) {
				public void processItem(final MetaInfo metaInfo) {
					if (metaInfo.isAnyFolder() || metaInfo.isFile())
						metaInfo.saveToFile();
				}
			};
			root.saveToFile();
		}
	}
	@Override
	protected void processKeyEvent(final KeyEvent e) {
				if ((e.getID() == KeyEvent.KEY_PRESSED) && (TK.isKeyStroke(e, VK_ENTER))) {
			openAtRow(e, getLeadSelectionRow());
			e.consume();
		}
		else {
			super.processKeyEvent(e);
		}
	}
	private Tree() {
		_instance = this;
		itemRenderer = new ItemRenderer();
		setCellRenderer(itemRenderer);
				setEditable(true);
		setCellEditor(new DefaultTreeCellEditor(this, null, new RenameEditor()));
		setInvokesStopCellEditing(true);
				mainRoot = MetaInfo.createMainRoot();
		setRootItem(mainRoot);
		setRootVisible(false);
				registerFS(TreeFS.getInstance());
		for (PluginInfo i : PluginType.FILE_SYSTEM.get()) {
			try {
				registerFS(FSPlugin.class.cast(i.getPluggable()).create());
			}
			catch (Exception exception) {
				MMessage.error(null, exception);
			}
		}
		initDragAndDrop();
		addKeyListener(new KeyAdapter() {
			@Override
			public void keyPressed(final KeyEvent e) {
				if (UI.isPopupTrigger(e))
					popupContextMenu(null);
			}
		} );
		addMouseListener(new MMouseAdapter() {
			@Override
			public void mouseClicked(final MouseEvent e) {
				DataTipManager.get().hide(Tree.this);
				int row = getRowForLocation(e.getPoint());
				if (row == -1) {
					e.consume();
					clearSelection();
					return;
				}
				if (
					(MAction.isTrigger(e) || isMiddle(e)) &&
										!e.isControlDown() && !e.isShiftDown()
				) {
					e.consume();
					openAtRow(e, row);
				}
			}
			@Override
			public void popupTrigger(final MouseEvent e) {
				popupContextMenu(e);
			}
		} );
		addMouseMotionListener(new MouseMotionAdapter() {
			@Override
			public void mouseMoved(final MouseEvent e) {
				if (!UI.singleClickOpen.get())
					return;
				int row = Tree.this.getRowForLocation(e.getPoint());
				Tree.this.itemRenderer.setMouseHover(Tree.this.getPathForRow(row));
				UI.setHandCursor(Tree.this, row != -1);
			}
		} );
	}
	private boolean dynamicOpen(final InputEvent e, final MetaInfo metaInfo) {
		AbstractFS fs = metaInfo.getFS();
		if (fs instanceof FSDynamic) {
			TreePath path = metaInfo.getTreePath();
			if (isExpanded(path) && (e != null)) {
				collapsePath(path);
				return true;
			}
			else {
				try {
					boolean result = FSDynamic.class.cast(fs).dynamic(0, metaInfo);
					metaInfo.reload(false);
					if (e != null)
						expandPath(path);
					return result;
				}
				catch (FSException exception) {
					MMessage.error(null, exception);
					return false;
				}
			}
		}
		return false;
	}
	private void initDragAndDrop() {
		setDragEnabled(true);
		dndTransferHandler = new DnDTransferHandler();
		setTransferHandler(dndTransferHandler);
		setDropMode(DropMode.ON);
		try {
			getDropTarget().addDropTargetListener(new DropTargetAdapter() {
				@Override
				public void dragEnter(final DropTargetDragEvent e) {
					dndTransferHandler.reset();
				}
				public void drop(final DropTargetDropEvent e) { }
			} );
		}
		catch (TooManyListenersException exception) {
			MLogger.exception(exception);
		}
	}
	private void openAtRow(final InputEvent e, final int index) {
		if (index == -1) {
			clearSelection();
			return;
		}
		MetaInfo metaInfo = getItemAtRow(index);
		if (metaInfo.isAnyFile() || metaInfo.isDynamicFolder()) {
			open(e, metaInfo);
		}
		else if (metaInfo.isFolder()) {
			open(e, metaInfo);
			if (UI.singleClickOpen.get()) {
				if (metaInfo.isLink())
					togglePath(metaInfo.getLink().getTreePath());
				else
					togglePath(metaInfo.getTreePath());
			}
		}
		else if (metaInfo.isFSRoot()) {
			dynamicOpen(null, metaInfo); 			if (UI.singleClickOpen.get())
				toggleRow(index);
		}
	}
	private void popupContextMenu(final InputEvent e) {
		Point p;
		if (e instanceof InputEvent) {
			p = MouseEvent.class.cast(e).getPoint();
						int rowUnderMouse = getRowForLocation(p);
			if (!isRowSelected(rowUnderMouse))
				setSelectionRow(rowUnderMouse);
		}
		else {
			p = new Point();
		}
		if (getSelectedItems().length > 0) {
			MMenu menu = TreePanel.getInstance().createTreeMenu(null, false);
			menu.showPopup(this, p);
		}
	}
	private void registerFS(final AbstractFS fs) {
		if (fileSystems.containsKey(fs.getID())) {
			MLogger.error("vfs", "File system is already registered: \"%s\"", fs);
			return;
		}
		MLogger.info("vfs", "Registering file system: \"%s\"", fs);
		fileSystems.put(fs.getID(), fs);
		addItem(mainRoot, fs.getRoot());
	}
	public static abstract class Scanner extends MTreeScanner<MetaInfo> {
		public Scanner() {
			super(Tree.getInstance().getRootItem());
		}
		public Scanner(final AbstractFS fs) {
			super(fs.getRoot());
		}
		public Scanner(final MetaInfo root) {
			super(root, PROCESS_ROOT);
		}
		public Scanner(final MetaInfo root, final boolean processRoot) {
			super(root, processRoot ? PROCESS_ROOT : 0);
		}
	}
