	public static void strikeThrough(final Component component, final Graphics graphics, final String text) {
		if (TK.isEmpty(text))
			return;
		Graphics2D g = (Graphics2D)graphics;
		g.setColor(deriveAlpha(RED, 127));
		int y1 = component.getHeight() / 2;
		FontMetrics metrics = g.getFontMetrics(component.getFont());
		int x2 = Math.min(component.getWidth() - 5, metrics.stringWidth(text));
		g.drawLine(5, y1, x2, y1);
	}
	public static BufferedImage toBufferedImage(final Image source) {
		BufferedImage result = createCompatibleImage(source.getWidth(null), source.getHeight(null), true);
		Graphics2D g = result.createGraphics();
		g.drawImage(source, 0, 0, null, null);
		g.dispose();
		return result;
	}
	public static void update(final MFrame frame) {
		new ContainerScanner(frame) {
			public void processComponent(final Container parent, final Component component) {
								if ((component instanceof JComponent) && !(component instanceof JTextComponent))
					JComponent.class.cast(component).updateUI();
			}
		};
	}
	public static Image waitForImage(final Image image) {
		MediaTracker mediaTracker = new MediaTracker(DummyComponent.getSharedInstance());
		mediaTracker.addImage(image, 0);
		try {
			mediaTracker.waitForID(0);
		}
		catch (InterruptedException exception) {
			MLogger.exception(exception);
		}
		return image;
	}
	@Uninstantiable private UI() { }
	private static int getLAFCode() {
		synchronized (UI.class) {
			if (lafCode == -1) {
				Object laf = UIManager.getLookAndFeel();
				String className = (laf == null) ? null : laf.getClass().getName();
				if ("com.sun.java.swing.plaf.gtk.GTKLookAndFeel".equals(className))
					lafCode = 4;
				else if (
					"com.sun.java.swing.plaf.windows.WindowsLookAndFeel".equals(className) ||
					"com.sun.java.swing.plaf.windows.WindowsClassicLookAndFeel".equals(className)
				)
					lafCode = 3;
				else if ("org.jvnet.substance.SubstanceLookAndFeel".equals(className))
					lafCode = 2;
				else if ((laf instanceof MetalLookAndFeel) && (MetalLookAndFeel.getCurrentTheme() instanceof OceanTheme))
					lafCode = 1;
				else
					lafCode = 0;
			}
		}
		return lafCode;
	}
	public static interface EventsControl {
		public void setEventsEnabled(final boolean value);
	}
