	private static int getLAFCode() {
		synchronized (UI.class) {
			if (lafCode == -1) {
				Object laf = UIManager.getLookAndFeel();
				String className = (laf == null) ? null : laf.getClass().getName();
				if ("com.sun.java.swing.plaf.gtk.GTKLookAndFeel".equals(className))
					lafCode = 4;
				else if (
					"com.sun.java.swing.plaf.windows.WindowsLookAndFeel".equals(className) ||
					"com.sun.java.swing.plaf.windows.WindowsClassicLookAndFeel".equals(className)
				)
					lafCode = 3;
				else if ("org.jvnet.substance.SubstanceLookAndFeel".equals(className))
					lafCode = 2;
				else if ((laf instanceof MetalLookAndFeel) && (MetalLookAndFeel.getCurrentTheme() instanceof OceanTheme))
					lafCode = 1;
				else
					lafCode = 0;
			}
		}
		return lafCode;
	}
