	public static void addInfo(final HTMLBuilder html, final String title, final Object info) {
		html.beginTag("tr");
			if (info == null) {
				html.doubleTag("td", html.escape(title), "colspan", 2);
			}
			else {
				html.doubleTag("td", html.escape(title), "align", "right");
				html.doubleTag("td", html.escape(info.toString()));
			}
		html.endTag("tr");
	}
	public static void addSeparator(final HTMLBuilder html) {
		html.appendLine("<tr><td colspan=\"2\"><hr></td></tr>");
	}
	public boolean canModify() {
		if (link != null)
			return link.canModify();
		return !locked && writeable;
	}
	public void clearIcon() {
		if (link != null) {
			link.clearIcon();
			return;
		}
		icon = null;
		iconName = null;
	}
	public Object clearProperty(final String key) {
		if (link != null)
			return link.clearProperty(key);
		if (properties == null)
			return null;
		Object result = properties.remove(key);
		if (properties.isEmpty())
			properties = null;
		return result;
	}
	public static MetaInfo createDummy(final String name, final Icon icon) {
		return createVirtualFile(null, name, icon);
	}
	public static MetaInfo createDynamicFolder(final AbstractFS fs, final String path) {
		return new MetaInfo(fs, path, DYNAMIC_FOLDER);
	}
	public static MetaInfo createFile(final AbstractFS fs, final String path, final PluginInfo info) {
		MetaInfo file;
		if (info == null) {
			file = new MetaInfo(fs, path, FILE | NOT_SUPPORTED);
		}
		else {
			file = new MetaInfo(fs, path, FILE);
			if (!file.isCustomIcon())
				file.setIcon(info.getIcon());
		}
		file.setAllowsChildren(false);
		file.setWriteable(file.getFile().canWrite());
		return file;
	}
	public static MetaInfo createFile(final AbstractFS fs, final String path, final String type) {
		return createFile(fs, path, EditorPlugin.findPluginForExtension(type));
	}
	public static MetaInfo createFSRoot(final AbstractFS fs, final String path) {
		return new MetaInfo(fs, path, FS_ROOT);
	}
	public static MetaInfo createLink(final MetaInfo metaInfo) {
		if (metaInfo.isLink())
			return null;
		MetaInfo link = new MetaInfo(metaInfo);
		link.setAllowsChildren(metaInfo.getAllowsChildren());
		return link;
	}
	public static MetaInfo createFolder(final AbstractFS fs, final String path, final String mount) {
		MetaInfo folder = new MetaInfo(fs, path, FOLDER);
				File directory = folder.getFile();
		folder.setWriteable(directory.canWrite());
		if (mount != null) {
			folder.setMount(mount);
			folder.saveToFile();
		}
				if (folder.isMount()) {
			try {
				MountManager.validate(folder.getMount());
				MountManager.mount(folder);
				folder.setWriteable(new File(folder.getMount()).canWrite());
			}
			catch (FSException exception) {
				MLogger.error("vfs", exception.getMessage());
				folder.setMount(null);
				folder.setProperty(OVERRIDE_ICON, FS.getIcon("ui/error"));
				folder.setProperty(OVERRIDE_NAME, "%name [" + exception.getMessage() + "]");
			}
		}
		return folder;
	}
	public static MetaInfo createMainRoot() {
		return new MetaInfo(null, FS.makeConfigPath("vfs"), 0);
	}
	public static MetaInfo createVirtualFile(final AbstractFS fs, final String name) {
		return createVirtualFile(fs, name, null);
	}
	public static MetaInfo createVirtualFile(final AbstractFS fs, final String name, final Icon icon) {
		MetaInfo virtualFile = new MetaInfo(fs, name, VIRTUAL_FILE);
		virtualFile.setAllowsChildren(false);
		if (icon != null)
			virtualFile.setIcon(icon);
		return virtualFile;
	}
	public long getAge() {
		if (link != null)
			return link.getAge();
		if (isVirtualFile())
			return -1;
		if (createdTime == 0) {
			long lm = getFile().lastModified();
			return (lm == 0) ? -1 : MDate.now().getDayCount(lm);
		}
		return MDate.now().getDayCount(createdTime);
	}
	public int getOrder() { return order; }
	public void setOrder(final int value) { order = value; }
	public float getRating() {
