	}
	public Config getConfig() {
		return (link == null) ? config : link.config;
	}
	public String getExtension() {
		if (link != null)
			return link.getExtension();
		return isFile() ? FS.getExtension(path) : null;
	}
	public File getFile() {
		return
			(link != null)
			? new File(link.path)
			: new File(path);
	}
	public String getFilePath() {
		return (link != null) ? link.path : path;
	}
	public void setFilePath(final String value) {
		if (link != null) {
			link.setFilePath(value);
			return;
		}
		if (isFSRoot()) {
			path = value;
			name = value;
		}
		else if (isVirtualFile()) {
			path = value;
			name = TK.escapeURL(value);
		}
		else {
			path = value;
			File file = getFile();
			if (isFile() || isDynamicFolder()) {
				name = getPathWithoutExtension(file.getName());
				if (config != null)
					config.setPath(path + ".properties");
			}
			else {
				name = file.getName();
			}
		}
	}
	public AbstractFS getFS() {
		return (link != null) ? link.fs : fs;
	}
	public void setFS(final AbstractFS value) {
		if (link != null) {
			link.setFS(value);
			return;
		}
		fs = value;
		if ((fs != null) && (config != null))
			config.write("fs", fs.getID());
	}
	public void setFSFromConfig() {
		if (link != null) {
			link.setFSFromConfig();
			return;
		}
		if (config != null) {
			AbstractFS fs = Tree.getInstance().getFS(config.read("fs", null));
			setFS(fs);
		}
	}
	public Icon getIcon() {
		if (link != null)
			return link.getIcon();
				if (icon == null) {
			if (isVirtualFile())
				return null;
						if (iconName == null)
				return null;
						icon = getIcon(iconName);
						if (icon == null) {
				iconName = "ui/misc";
				icon = FS.getIcon(iconName);
			}
		}
		if ((icon != null) && !canModify()) {
			if (lockedIconCache != null)
				return lockedIconCache;
			int w = icon.getIconWidth() / 2;
			int h = icon.getIconHeight() / 2;
			if (lockedMiniIcon == null)
				lockedMiniIcon = UI.scaleImage(FS.getImage("ui/locked"), w, h, true);
			BufferedImage image = UI.createCompatibleImage(icon.getIconWidth(), icon.getIconHeight(), true);
						Graphics2D g = image.createGraphics();
			icon.paintIcon(null, g, 0, 0);
			g.drawImage(lockedMiniIcon, w, h, null, null);
			lockedIconCache = new ImageIcon(image);
			lockedIconCache.setDescription(icon.toString());
			g.dispose();
			return lockedIconCache;
		}
		return icon;
	}
	public void setIcon(final Icon value) {
		if (link != null) {
			link.setIcon(value);
			return;
		}
		clearProperty(OVERRIDE_ICON);
		if (value == null) {
			if (isDynamicFolder())
				iconName = "dynamicfolder";
			else if (isFolder())
				iconName = "ui/folder";
			else
				iconName = "ui/file";
			icon = FS.getIcon(iconName);
		}
		else {
			iconName = null;
			icon = value;
		}
		lockedIconCache = null;
	}
	public MetaInfo getLink() { return link; }
