	public String getNicePath() {
		if (link != null)
			return link.getNicePath();
		if (isVirtualFile()) {
			MetaInfo parentFolder = getParentFolder();
			if (parentFolder == null)
				return path;
			return parentFolder.getNicePath() + " > " + path;
		}
		String dir;
		if (isFSRoot() || isRoot()) {
			dir = path;
		}
		else if (path.startsWith(fs.getDirectory())) {
			dir = path.substring(fs.getDirectory().length() + 1);
		}
		else {
			dir = path;
		}
		return fs.getName() + " > " + TK.unescapeURL(getPathWithoutExtension(dir).replace(File.separator, " > "));
	}
	public MetaInfo getParentFolder() {
		if (link != null)
			return link.getParentFolder();
		return (MetaInfo)getParentItem();
	}
	@SuppressWarnings("unchecked")
	public <T> T getProperty(final String key, final T defaultValue) {
		if (link != null)
			return link.getProperty(key, defaultValue);
		if (properties == null)
			return defaultValue;
		Object result = properties.get(key);
		return (result == null) ? defaultValue : (T)result;
	}
	public Object setProperty(final String key, final Object value) {
		if (link != null)
			return link.setProperty(key, value);
		if (properties == null)
			properties = new HashMap<String, Object>();
		return properties.put(key, value);
	}
	public float getRatio() {
		if (link != null)
			return link.getRatio();
		long age = getAge();
		if (age <= 0)
			return 0.0f;
		return isFolder() ? -1.0f : (getUsage() / (float)age);
	}
