	public MetaInfo[] getSubMetaInfos(final MV modelView) {
		MetaInfo[] result = new MetaInfo[getChildCount(modelView)];
		for (int i = 0; i < result.length; i++)
			result[i] = (MetaInfo)getChildAt(i, modelView);
		return result;
