	public String getToolTipText() {
		return getToolTipText(0);
	}
	public String getToolTipText(final int aFlags) {
		if (link != null)
			return link.getToolTipText(aFlags);
		Flags flags = new Flags(aFlags);
		HTMLBuilder builder = new HTMLBuilder();
		builder.beginHTML();
		builder.beginDoc();
		builder.beginTag(
			"table",
			"border", 0
		);
				addInfo(builder, TK.centerSqueeze(toString(), 128), null);
				String type = getExtension();
				if (type == null) {
			if (isDynamicFolder())
				type = _("Virtual Folder");
			else if (isVirtualFile())
				type = _("Virtual File");
			else
				type = _("Folder");
		}
				else {
			if (info == null)
				info = EditorPlugin.findPluginForExtension(type);
			type = (info == null) ? _("Unknown") : info.name.get();
		}
		addInfo(builder, _("Type:"), type);
		if (!TK.isEmpty(comment) && flags.isClear(NO_COMMENT_INFO)) {
						int n = comment.indexOf('\n');
			String s = (n == -1) ? comment : (comment.substring(0, n) + "...");
			addInfo(builder, _("Comment:"), TK.centerSqueeze(s, 128));
		}
		if ((tags != null) && flags.isClear(NO_TAGS_INFO))
			addInfo(builder, _("Tags:"), tags);
		if (flags.isClear(NO_DATE_INFO)) {
			if (createdTime != 0)
				addInfo(builder, _("Created:"), formatDate(createdTime));
			if (isFile())
				addInfo(builder, _("Modified:"), formatDate(getFile().lastModified()));
		}
		if (flags.isClear(NO_LOCATION_INFO))
			addInfo(builder, _("Location:"), TK.centerSqueeze(getNicePath(), 128));
		if (mount != null)
			addInfo(builder, _("Mounted External Directory:"), mount);
		if (!isWriteable())
			builder.appendLine("<tr><td colspan=\"2\"><font color=\"#ff0000\">" + _("This item is not writeable (read only)") + "</font></td></tr>");
		else if (isLocked() && flags.isClear(NO_LOCK_INFO))
			builder.appendLine("<tr><td colspan=\"2\"><font color=\"#ff0000\">" + _("This item is locked (read only)") + "</font></td></tr>");
		if (flags.isClear(NO_DATE_INFO)) {
			boolean isSeparator = false;
			if (isFile() && (openTime != 0)) {
				addSeparator(builder);
				isSeparator = true;
				addInfo(builder, _("Last Opened:"), formatDate(openTime));
			}
			long age = getAge();
			if (age != -1) {
				if (!isSeparator)
					addSeparator(builder);
				addInfo(builder, _("Age:"), _("{0} day(s)", age));
			}
		}
		if (getFS() instanceof FSProperties)
			FSProperties.class.cast(getFS()).updateInfo(this, builder);
		builder.endTag("table");
		builder.endDoc();
		return builder.toString();
	}
