	}
	public void saveToFile() {
		if (link != null) {
			link.saveToFile();
			return;
		}
		if (config == null)
			return;
		if (!writeable)
			return;
		config.removeString("keywords");
		config.write("bookmark", bookmark);
		config.write("color", color);
		config.write("comment", comment);
		config.write("created", new Date(createdTime));
		if (iconName == null)
			config.removeString("icon");
		else
			config.write("icon", iconName);
		config.write("lastOpen", new Date(openTime));
		config.write("locked", locked);
		config.write("order", order);
		config.write("rating", rating);
		if (TK.isEmpty(tags))
			config.removeString("tags");
		else
			config.write("tags", tags);
		config.write("usage", usage);
		config.write("version", VERSION);
				if (isFolder()) {
			if (mount == null)
				config.removeString("mount");
			else
				config.write("mount", mount);
			config.write("permission.read", readPermission);
			config.write("permission.write", writePermission);
		}
		config.save();
	}
	public void setCreatedTime() {
		setCreatedTime(MDate.now());
	}
	public void setCreatedTime(final Date value) {
		long time = value.getTime();
		if (link != null)
			link.createdTime = time;
		else
			createdTime = time;
	}
	public void setExtraInfo(final Object info) {
		if (info == null) {
			clearProperty(EXTRA_COLOR);
			clearProperty(EXTRA_TEXT);
		}
		else {
			setProperty(EXTRA_COLOR, BLUE);
			setProperty(EXTRA_TEXT, "[" + info + "]");
		}
	}
	public void setLastOpenTime() {
		if (link != null) {
			link.setLastOpenTime();
			return;
		}
		openTime = MDate.currentTime();
		usage++;
	}
	private static String formatDate(final long date) {
		if (date == 0)
			return null;
		return DT.format(date, MDate.FULL, MDate.SHORT);
	}
	private String getPathWithoutExtension(final String pathWithExtension) {
		if (link != null)
			return link.getPathWithoutExtension(pathWithExtension);
		if (isFolder())
			return pathWithExtension;
		int i = pathWithExtension.lastIndexOf(".");
		if (i != -1)
			return pathWithExtension.substring(0, i);
		return pathWithExtension;
	}
	public String getIconName() {
		return (link != null) ? link.iconName : iconName;
	}
	public void setIconName(final String value) {
		if (link != null) {
			link.setIconName(value);
			return;
		}
		clearProperty(OVERRIDE_ICON);
		iconName = value;
		icon = getIcon(iconName);
		if (icon == null) {
						setIcon(null);
			return;
		}
		lockedIconCache = null;
	}
	public void reload(final boolean onlyIfFilter) {
		final Tree tree = Tree.getInstance();
		if (onlyIfFilter && !tree.getDefaultModel().isFilter())
			return;
		boolean expanded = tree.isExpanded(this);
		tree.getDefaultModel().reload(this);
		if (expanded)
			tree.setExpanded(this, true);
	}
