		config.write("bookmark", bookmark);
		config.write("color", color);
		config.write("comment", comment);
		config.write("created", new Date(createdTime));
		if (iconName == null)
			config.removeString("icon");
		else
			config.write("icon", iconName);
		config.write("lastOpen", new Date(openTime));
		config.write("locked", locked);
		config.write("order", order);
		config.write("rating", rating);
		if (TK.isEmpty(tags))
			config.removeString("tags");
		else
			config.write("tags", tags);
		config.write("usage", usage);
		config.write("version", VERSION);
				if (isFolder()) {
			if (mount == null)
				config.removeString("mount");
			else
				config.write("mount", mount);
			config.write("permission.read", readPermission);
			config.write("permission.write", writePermission);
		}
		config.save();
	}
	public void setCreatedTime() {
		setCreatedTime(MDate.now());
	}
	public void setCreatedTime(final Date value) {
		long time = value.getTime();
		if (link != null)
			link.createdTime = time;
		else
			createdTime = time;
	}
	public void setExtraInfo(final Object info) {
		if (info == null) {
			clearProperty(EXTRA_COLOR);
			clearProperty(EXTRA_TEXT);
		}
		else {
			setProperty(EXTRA_COLOR, BLUE);
			setProperty(EXTRA_TEXT, "[" + info + "]");
		}
	}
	public void setLastOpenTime() {
		if (link != null) {
			link.setLastOpenTime();
			return;
		}
		openTime = MDate.currentTime();
		usage++;
	}
	private static String formatDate(final long date) {
		if (date == 0)
			return null;
		return DT.format(date, MDate.FULL, MDate.SHORT);
	}
	private String getPathWithoutExtension(final String pathWithExtension) {
		if (link != null)
			return link.getPathWithoutExtension(pathWithExtension);
		if (isFolder())
			return pathWithExtension;
		int i = pathWithExtension.lastIndexOf(".");
		if (i != -1)
			return pathWithExtension.substring(0, i);
		return pathWithExtension;
	}
	public String getIconName() {
		return (link != null) ? link.iconName : iconName;
	}
	public void setIconName(final String value) {
		if (link != null) {
			link.setIconName(value);
			return;
		}
		clearProperty(OVERRIDE_ICON);
		iconName = value;
		icon = getIcon(iconName);
		if (icon == null) {
						setIcon(null);
			return;
		}
		lockedIconCache = null;
	}
	public void reload(final boolean onlyIfFilter) {
		final Tree tree = Tree.getInstance();
		if (onlyIfFilter && !tree.getDefaultModel().isFilter())
			return;
		boolean expanded = tree.isExpanded(this);
		tree.getDefaultModel().reload(this);
		if (expanded)
			tree.setExpanded(this, true);
	}
	@Important
	@Override
	public String toString() {
				if (link != null)
			return link.toString();
		if (isFSRoot())
			return fs.getName();
		return TK.unescapeURL(name);
	}
	private MetaInfo(final MetaInfo link) {
		super(Tree.getInstance().getDefaultModel());
		this.link = link;
	}
	private MetaInfo(final AbstractFS fs, final String path, final int attr) {
		super(Tree.getInstance().getDefaultModel());
		this.fs = fs;
		this.attr = attr;
		setFilePath(path);
		loadFromFile();
		if (fs == null)
			setFSFromConfig();
		else
			setFS(fs);
	}
	private String getConfigPath() {
		if (link != null)
			return link.getConfigPath();
		if (isDynamicFolder())
			return path;
		if (isVirtualFile())
			return null; 
		if (isFile())
			return (path + ".properties");
		if (isFolder() || isFSRoot())
			return FS.makePath(path, ".properties");
		return null;
	}
	private Icon getIcon(final String path) {
		if (path == null)
			return null;
		if (path.startsWith("file:/")) {
			try {
				ImageIcon result = new ImageIcon(new URL(path).getPath());
				return UI.isEmpty(result) ? null : result;
			}
			catch (MalformedURLException exception) {
				MLogger.exception(exception);
				return null;
			}
		}
		else {
			return FS.getIcon(path);
		}
	}
	private void loadFromFile() {
		if (link != null) {
			link.loadFromFile();
			return;
		}
		String configPath = getConfigPath();
		config = (configPath == null) ? null : new Config(configPath, (writeable || isMount()) ? 0 : Config.READ_ONLY);
		if (config == null)
			return;
		bookmark = config.read("bookmark", false);
		color = config.readColor("color", null);
		comment = config.read("comment", null);
		createdTime = config.readDate("created", 0).getTime();
		if (createdTime == 0)
			createdTime = getFile().lastModified();
		locked = config.read("locked", false); 		
		String defaultIconName;
		if (isDynamicFolder())
			defaultIconName = "dynamicfolder";
		else if (isFolder())
			defaultIconName = "ui/folder";
		else
			defaultIconName = null;
		iconName = config.read("icon", defaultIconName);
				if (iconName != null) {
			String newIconName = oldIconNameMap.get(iconName);
			if (newIconName != null)
				iconName = newIconName;
		}
		openTime = config.readDate("lastOpen", 0).getTime();
		order = config.readInt("order", 0);
		rating = config.readFloat("rating", 0.0f);
		tags = config.read("tags", null);
		usage = config.readInt("usage", 0);
				if (isFolder()) {
			mount = config.read("mount", null);
			readPermission = config.read("permission.read", false);
			writePermission = config.read("permission.write", false);
		}
	}
