"package org.makagiga.commons.autocompletion;
import static java.awt.Color.*;
import static java.awt.event.KeyEvent.*;
import java.awt.AWTEvent;
import java.awt.BorderLayout;
import java.awt.Point;
import java.awt.Toolkit;
import java.awt.Window;
import java.awt.event.AWTEventListener;
import java.awt.event.ComponentEvent;
import java.awt.event.FocusEvent;
import java.awt.event.KeyAdapter;
import java.awt.event.KeyEvent;
import java.awt.event.MouseEvent;
import javax.swing.BorderFactory;
import javax.swing.JWindow;
import javax.swing.SwingUtilities;
import javax.swing.border.BevelBorder;
import javax.swing.event.DocumentEvent;
import javax.swing.event.DocumentListener;
import javax.swing.text.JTextComponent;
import org.makagiga.commons.*;
public final class AutoCompletion {
	public final BooleanProperty caseSensitive = new BooleanProperty();
	public final BooleanProperty consumeEnter = new BooleanProperty(true);
	public final BooleanProperty enabled = new BooleanProperty(true);
	private AutoCompletionData data;
	private JTextComponent textComponent;
	private String id;
	public AutoCompletion(final JTextComponent textComponent, final String id) {
		this.textComponent = textComponent;
		this.id = id;
		install();
	}
	public void addItem(final String text) {
		init();
		data.add(text);
	}
	public void saveToFile() {
		if (data != null)
			data.save();
	}
	private void complete() {
		if (!enabled.get())
			return;
		String text = textComponent.getText();
		if (TK.isEmpty(text))
			return;
		if (!caseSensitive.get())
			text = text.toUpperCase();
		init();
		if (data.getList() == null)
			return;
		final CompletionWindow window = CompletionWindow.getInstance();
		window.list.clear();
		for (AutoCompletionData.Item i : data.getList()) {
			if (i.matches(text, caseSensitive.get()))
				window.list.addItem(i);
		}
		if (window.list.isEmpty()) {
			CompletionWindow.freeInstance();
		}
		else {
			Point location = SwingUtilities.convertPoint(textComponent, window.getLocation(), window);
			window.setLocation(location.x, location.y + textComponent.getHeight());
			window.setSize(Math.max(textComponent.getWidth(), 200), 100);
			if (!window.isVisible()) {
				if (UI.specialEffects.get())
					Blend.border(window.getRootPane(), BLUE, WHITE);
				else
					window.getRootPane().setBorder(BorderFactory.createBevelBorder(BevelBorder.RAISED));
			}
			window.setVisible(true);
		}
	}
	private void init() {
		if (data == null) {
			data = AutoCompletionData.load(id);
			if (data == null)
				data = new AutoCompletionData(id);
		}
	}
	private void install() {
		textComponent.getDocument().addDocumentListener(new DocumentListener() {
			public void changedUpdate(final DocumentEvent e) { }
			public void insertUpdate(final DocumentEvent e) {
				complete();
			}
			public void removeUpdate(final DocumentEvent e) {
				complete();
			}
		} );
		textComponent.addKeyListener(new KeyAdapter() {
			@Override
			public void keyPressed(final KeyEvent e) {
				if (TK.isKeyStroke(e, VK_ENTER)) {
					if (CompletionWindow.isInstance()) {
						AutoCompletionData.Item item = CompletionWindow.getInstance().list.getSelectedItem();
						if (item != null) {
							item.usage++;
							enabled.no();
							try {
								textComponent.setText(item.text);
							}
							finally {
								enabled.yes();
							}
							if (consumeEnter.get())
								e.consume();
						}
						CompletionWindow.freeInstance();
					}
				}
				else if (TK.isKeyStroke(e, VK_ESCAPE)) {
					if (CompletionWindow.isInstance()) {
						CompletionWindow.freeInstance();
						e.consume();
					}
				}
				else if (TK.isKeyStroke(e, VK_UP)) {
					if (CompletionWindow.isInstance()) {
						CompletionWindow.getInstance().selectRelativeIndex(-1);
						enabled.no();
						try {
							AutoCompletionData.Item item = CompletionWindow.getInstance().list.getSelectedItem();
							if (item != null)
								textComponent.setText(item.text);
						}
						finally {
							enabled.yes();
						}
						e.consume();
					}
				}
				else if (TK.isKeyStroke(e, VK_DOWN)) {
					if (CompletionWindow.isInstance()) {
						CompletionWindow.getInstance().selectRelativeIndex(+1);
						enabled.no();
						try {
							AutoCompletionData.Item item = CompletionWindow.getInstance().list.getSelectedItem();
							if (item != null)
								textComponent.setText(item.text);
						}
						finally {
							enabled.yes();
						}
						e.consume();
					}
				}
			}
		} );
		textComponent.addMouseListener(new MMouseAdapter() {
			@Override
			public void mouseClicked(final MouseEvent e) {
				if (CompletionWindow.isInstance()) {
					CompletionWindow.freeInstance();
					e.consume();
				}
			}
		} );
	}
	private static final class CompletionWindow extends JWindow {
		private static AWTEventListener awtListener;
		private static CompletionWindow _instance;
		private MList<AutoCompletionData.Item> list;
		public static void freeInstance() {
			if (_instance != null) {
				_instance.dispose();
				_instance = null;
			}
		}
		public synchronized static CompletionWindow getInstance() {
			if (_instance == null)
				_instance = new CompletionWindow();
			return _instance;
		}
		public static boolean isInstance() {
			return _instance != null;
		}
		public void selectAbsoluteIndex(final int index) {
			try {
				list.setSelectedIndex(index);
				list.ensureIndexIsVisible(index);
			}
			catch (IndexOutOfBoundsException exception) { } 		}
		public void selectRelativeIndex(final int delta) {
			selectAbsoluteIndex(list.getSelectedIndex() + delta);
		}
		private CompletionWindow() {
						list = new MList<AutoCompletionData.Item>() {
				@Override
				protected void onAction(final AutoCompletionData.Item item) {
				}
			};
			list.setSingleSelectionMode();
			MScrollPane scrollPane = new MScrollPane(list);
			scrollPane.setBorder(null);
			add(scrollPane, BorderLayout.CENTER);
						if (awtListener == null) {
				awtListener = new AWTEventListener() {
					public void eventDispatched(final AWTEvent e) {
												if (e instanceof FocusEvent) {
							freeInstance();
						}
												else if (e instanceof ComponentEvent) {
							ComponentEvent ce = (ComponentEvent)e;
							if (
								(ce.getComponent() instanceof Window) &&
								(
									(ce.getID() == ComponentEvent.COMPONENT_HIDDEN) ||
									(ce.getID() == ComponentEvent.COMPONENT_MOVED)
								) &&
								(ce.getComponent() != _instance)
							) {
								freeInstance();
							}
						}
					}
				};
				Toolkit.getDefaultToolkit().addAWTEventListener(awtListener, AWTEvent.COMPONENT_EVENT_MASK | AWTEvent.FOCUS_EVENT_MASK);
			}
		}
	}
