	public MetaInfo getNextItem(final MetaInfo baseItem) {
		MetaInfo parent = (MetaInfo)baseItem.getParentItem();
		if (parent == null)
			return null;
		MetaInfo[] files = parent.getSubMetaInfos(MV.VIEW);
		if (files.length < 2)
			return null;
		boolean afterCurrent = false;
		for (int i = 0; i < files.length; i++) {
			if (afterCurrent && files[i].isAnyFile())
				return files[i];
			if (files[i] == baseItem)
				afterCurrent = true;
		}
		return null;
	}
	public MetaInfo getPreviousItem(final MetaInfo baseItem) {
		MetaInfo parent = (MetaInfo)baseItem.getParentItem();
		if (parent == null)
			return null;
		MetaInfo[] files = parent.getSubMetaInfos(MV.VIEW);
		if (files.length < 2)
			return null;
		boolean beforeCurrent = false;
		for (int i = files.length - 1; i >= 0; --i) {
			if (beforeCurrent && files[i].isAnyFile())
				return files[i];
			if (files[i] == baseItem)
				beforeCurrent = true;
		}
		return null;
	}
