	public void installTabBar(final JComponent c) {
		if (tabBar == c)
			return;
		boolean shouldValidate = false;
				if (tabBar != null) {
			tabPanel.remove(tabBar);
			shouldValidate = true;
		}
				tabBar = c;
		if (tabBar != null) {
			tabBar.setVisible(true);
			tabPanel.addSouth(tabBar);
			shouldValidate = true;
		}
		if (shouldValidate) {
			tabPanel.validate();
			if (tabBar != null)
				tabBar.repaint(); 		}
	}
	public void setDesktop(final boolean value, final boolean effect) {
		desktop = value;
		mainPanel.setFrontVisible(!desktop, effect);
		toggleDesktopAction.setEnabled(!Tabs.getInstance().isEmpty());
		updateState();
		if (desktop)
			DesktopPanel.getInstance().focus();
	}
