	public void navigate(final boolean next) {
		final Tabs tabs = Tabs.getInstance();
		Editor<?> currentEditor = tabs.getSelectedTab();
		if (currentEditor == null)
			return;
		MetaInfo baseFile = currentEditor.getMetaInfo();
		MetaInfo newFile = next ? getNextItem(baseFile) : getPreviousItem(baseFile);
		if (newFile != null) {
			int index = tabs.findEditor(baseFile);
						tabs.closeEditorAt(index, Tabs.REMOVE_TAB | Tabs.SAVE_TO_FILE);
						Tree.getInstance().open(newFile);
		}
	}
	@Override
