			if (object instanceof MAbout) {
				MAbout about = MAbout.class.cast(object);
				about.addCredit("The AB5k Team", "AB5k Widgets", "http:				about.addCredit("Amarok Team - Rediscover Your Music!", "Blend Algorithm", "http:				about.addCredit("Everaldo Coelho and KDE Team", "Crystal SVG Icon Theme", "http:				about.addCredit("Jerry Huxtable", "Image Filters", "http:				about.addCredit("M. Korbar", "German Translation, Ideas, Testing", "gmx\u002enet", "lopoff");
				about.addCredit("Romain Guy", "Java/Swing Special Effects", "http:				about.addCredit("Santhosh Kumar", "Java/Swing Ideas", "http:				about.addCredit("Scott Violet", "Ocean Gradient Customizer", "http:				about.addCredit("Portland", "Xdg-utils", "http:				about.addCredit("SourceForge.net", "Project Hosting", "http:				about.addCredit("Yellowicon Studio", "\"Emotion\" Icon Set", "http:			}
			else if (object instanceof MouseGestures) {
				final Tabs tabs = Tabs.getInstance();
				final TreePanel treePanel = TreePanel.getInstance();
				MouseGestures mouseGestures = MouseGestures.class.cast(object);
				mouseGestures.registerGesture("D", treePanel.getNewFileAction());
				mouseGestures.registerGesture("Dcs", MApplication.getQuitAction());
