			setSelected(true);
		}
		@Override
		public void onAction() {
			MStatusBar.getGlobal().setClosed(!isSelected());
		}
	}
	private static final class ToggleDesignerAction extends MAction {
		public ToggleDesignerAction() {
			super(_("Toggle Designer"), "designer", VK_F4);
		}
		@Override
		public void onAction() {
			showSidebar();
			final Sidebar sidebar = Sidebar.getInstance();
			if (sidebar.isDesignerSelected())
				sidebar.selectLastTab();
			else
				sidebar.goTo(SidebarTab.DESIGNER);
		}
	}
	private final class ToggleDesktopAction extends MAction {
		public ToggleDesktopAction() {
			super(_("Toggle Desktop"), "desktop", VK_F5);
		}
		@Override
		public void onAction() {
			setDesktop(!DesktopPanel.getInstance().isVisible(), true);
		}
	}
	private static final class ToggleSummaryAction extends MAction {
		public ToggleSummaryAction() {
			super(_("Toggle Summary"), "summary", VK_S, CTRL_MASK + SHIFT_MASK);
		}
		@Override
		public void onAction() {
			showSidebar();
			final Sidebar sidebar = Sidebar.getInstance();
			if (sidebar.isSummarySelected())
				sidebar.selectLastTab();
			else
				sidebar.goTo(SidebarTab.SUMMARY);
		}
	}
	private final class ZoomAction extends MAction {
		private ZoomType type;
		public ZoomAction(final ZoomType type) {
			this.type = type;
			switch (type) {
				case IN:
					setAcceleratorKey(VK_EQUALS, CTRL_MASK);
					setIcon("ui/plus");
					setName(_("Zoom In"));
					break;
				case OUT:
					setAcceleratorKey(VK_MINUS, CTRL_MASK);
					setIcon("ui/minus");
					setName(_("Zoom Out"));
					break;
			}
		}
		@Override
		public void onAction() {
			Editor<?> editor = Tabs.getInstance().getSelectedTab();
			if (editor instanceof EditorZoom) {
				EditorZoom editorZoom = (EditorZoom)editor;
				editorZoom.zoom(type);
				MainWindow.this.zoomInAction.setEnabled(editorZoom.isZoomEnabled(EditorZoom.ZoomType.IN));
				MainWindow.this.zoomOutAction.setEnabled(editorZoom.isZoomEnabled(EditorZoom.ZoomType.OUT));
			}
		}
	}
