		}
		@Override
		public void onAction() {
			mainToolBar.setVisible(isSelected());
		}
	}
	private final class ShowHidePropertiesBarAction extends MAction {
		@Override
		public void onAction() {
			setInstalled(isSelected());
		}
		private ShowHidePropertiesBarAction() {
			super(_("Properties Bar"));
		}
		private void setInstalled(final boolean yes) {
			setSelected(yes);
			if (yes) {
				final PropertiesBar propertiesBar = PropertiesBar.getInstance();
				propertiesBar.setMetaInfo(Tabs.getInstance().getCurrentMetaInfo());
				installTabBar(propertiesBar);
			}
			else {
				installTabBar(null);
				PropertiesBar.freeInstance();
			}
		}
	}
	private final class ShowHideSidebarAction extends MAction {
		ShowHideSidebarAction() {
			super(_("Toggle Sidebar"), null, VK_F9);
		}
		@Override
		public void onAction() {
			mainSplitPane.toggleSidebar();
		}
