		while (scanner.hasNext()) {
			token = scanner.next();
			valueIndex = token.indexOf(':');
			if (valueIndex == -1)
				error("\":\" expected");
			property = token.substring(0, valueIndex).trim();
			value = token.substring(valueIndex + 1).trim();
			if ("background-color".equals(property)) {
				backgroundColor = parseColor(value);
			}
			else if ("border-style".equals(property)) {
				if ("inset".equals(value))
					border = BorderFactory.createLoweredBevelBorder();
				else if ("outset".equals(value))
					border = BorderFactory.createRaisedBevelBorder();
				else
					error("Invalid \"border-style\" value: " + value);
