					setCursor(Cursor.DEFAULT_CURSOR);
				else if ("move".equals(value))
					setCursor(Cursor.MOVE_CURSOR);
				else if ("pointer".equals(value))
					setCursor(Cursor.HAND_CURSOR);
				else if ("text".equals(value))
					setCursor(Cursor.TEXT_CURSOR);
				else
					error("Invalid \"cursor\" value: " + value);
			}
			else if ("font-family".equals(property)) {
				fontName = value;
			}
			else if ("font-size".equals(property)) {
				if ("smaller".equals(value))
					fontSize--;
				else if ("larger".equals(value))
					fontSize++;
				else if ("x-large".equals(value))
					fontSize += 2;
				else if ("xx-large".equals(value))
					fontSize += 10;
				else
					fontSize = parseSize(value);
			}
			else if ("font-style".equals(property)) {
				if ("italic".equals(value)) {
					fontStyle |= Font.ITALIC;
					fontStyle &= ~Font.PLAIN;
				}
				else if ("normal".equals(value))
					fontStyle &= ~Font.ITALIC;
				else
					error("Invalid \"font-style\" value: " + value);
			}
			else if ("font-weight".equals(property)) {
				if ("bold".equals(value)) {
					fontStyle |= Font.BOLD;
					fontStyle &= ~Font.PLAIN;
				}
				else if ("normal".equals(value))
					fontStyle &= ~Font.BOLD;
				else
					error("Invalid \"font-weight\" value: " + value);
			}
			else if ("margin".equals(property)) {
				Insets insets = parseInsets(value);
				if (insets != null) {
					margin = insets;
					marginSingleValueSet = false;
				}
				else {
					int i = parseSize(value);
					margin = UI.createInsets(i);
					marginSingleValueSet = false;
				}
			}
			else if ("margin-bottom".equals(property)) {
				marginBottom = parseSize(value);
				marginSingleValueSet = true;
			}
			else if ("margin-left".equals(property)) {
				marginLeft = parseSize(value);
				marginSingleValueSet = true;
			}
			else if ("margin-right".equals(property)) {
				marginRight = parseSize(value);
				marginSingleValueSet = true;
			}
			else if ("margin-top".equals(property)) {
				marginTop = parseSize(value);
				marginSingleValueSet = true;
			}
			else if ("padding".equals(property)) {
				Insets insets = parseInsets(value);
				if (insets != null) {
					padding = insets;
					paddingSingleValueSet = false;
				}
				else {
					int i = parseSize(value);
					padding = UI.createInsets(i);
					paddingSingleValueSet = false;
				}
			}
			else if ("padding-bottom".equals(property)) {
				paddingBottom = parseSize(value);
				paddingSingleValueSet = true;
			}
			else if ("padding-left".equals(property)) {
				paddingLeft = parseSize(value);
				paddingSingleValueSet = true;
			}
			else if ("padding-right".equals(property)) {
				paddingRight = parseSize(value);
				paddingSingleValueSet = true;
			}
			else if ("padding-top".equals(property)) {
				paddingTop = parseSize(value);
				paddingSingleValueSet = true;
