				colorNames = new HashMap<String, Color>(16);
								colorNames.put("aqua", Color.CYAN);
				colorNames.put("black", Color.BLACK);
				colorNames.put("blue", Color.BLUE);
				colorNames.put("fuchsia", Color.MAGENTA);
				colorNames.put("gray", Color.GRAY);
				colorNames.put("green", new Color(0x008000));
				colorNames.put("lime", Color.GREEN);
				colorNames.put("maroon", new Color(0x800000));
				colorNames.put("navy", new Color(0x000080));
				colorNames.put("olive", new Color(0x808000));
				colorNames.put("purple", new Color(0x800080));
				colorNames.put("red", Color.RED);
				colorNames.put("silver", new Color(0xC0C0C0));
				colorNames.put("teal", new Color(0x008080));
				colorNames.put("white", Color.WHITE);
				colorNames.put("yellow", Color.YELLOW);
			}
			result = colorNames.get(value);
		}
		if (result == null)
			result = UIManager.getColor(value);
		return result;
	}
	protected Insets parseInsets(final String value) throws ParseException {
		if (!value.contains(" "))
			return null;
		String values[] = value.split(" ");
		if (values.length != 4)
			error("Expected 4, space-separated values: " + value);
		Insets insets = UI.createInsets(0);
				insets.top = parseSize(values[0]);
		insets.right = parseSize(values[1]);
		insets.bottom = parseSize(values[2]);
		insets.left = parseSize(values[3]);
		return insets;
	}
	protected int parseSize(final String value) throws ParseException {
		try {
			int pxIndex = value.indexOf("px");
			return Integer.parseInt((pxIndex == -1) ? value : value.substring(0, pxIndex));
		}
		catch (NumberFormatException exception) {
			error("Invalid size value: " + value);
			return -1;
		}
	}
