		String values[] = value.split(" ");
		if (values.length != 4)
			error("Expected 4, space-separated values: " + value);
		Insets insets = UI.createInsets(0);
				insets.top = parseSize(values[0]);
		insets.right = parseSize(values[1]);
		insets.bottom = parseSize(values[2]);
		insets.left = parseSize(values[3]);
		return insets;
	}
	protected int parseSize(final String value) throws ParseException {
		try {
			int pxIndex = value.indexOf("px");
			return Integer.parseInt((pxIndex == -1) ? value : value.substring(0, pxIndex));
		}
		catch (NumberFormatException exception) {
			error("Invalid size value: " + value);
			return -1;
		}
	}
