	private static LookAndFeelType lookAndFeelType;
	private static SimpleButtonUI simpleButtonUI;
	public static String _(final String id) {
		return Gettext.translate(id);
	}
	public static String _(final String id, final Object... args) {
		try {
			return MessageFormat.format(Gettext.translate(id), args);
		}
		catch (IllegalArgumentException exception) {
			MLogger.error("core", "Invalid text/message format: \"%s\"", id);
			MLogger.exception(exception);
			return MessageFormat.format(id, args);
		}
	}
	public static void addCenter(final Container parent, final JComponent component) {
		if (
			(component instanceof Scrollable) &&
			!(component instanceof MScrollPane) &&
			!(component instanceof JTextField)
		)
			parent.add(new MScrollPane(component), BorderLayout.CENTER);
		else
			parent.add(component, BorderLayout.CENTER);
	}
	public static Font changeFontSize(final Component component, final int relativeSize, final int minimum, final int maximum) {
		Font oldFont = component.getFont();
		if (relativeSize == 0)
			return oldFont;
		int newSize = oldFont.getSize() + relativeSize;
		if (minimum != -1)
			newSize = Math.max(newSize, minimum);
		if (maximum != -1)
			newSize = Math.min(newSize, maximum);
		Font newFont = oldFont.deriveFont((float)newSize);
		component.setFont(newFont);
		return newFont;
	}
	public static Border createDropShadowBorder() {
		if (!specialEffects.get())
			return null;
		if (noDropShadowClass)
			return null;
		try {
			return TK.newInstance("org.jdesktop.swingx.border.DropShadowBorder");
		}
		catch (Exception exception) { 			noDropShadowClass = true;
			return null;
		}
	}
	public static Border createEmptyBorder(final int size) {
		return BorderFactory.createEmptyBorder(size, size, size, size);
	}
	public static List<Object> createGradient(final Color color1, final Color color2) {
		return createGradient(1.0f, 0.0f, color1, color2);
	}
				public static List<Object> createGradient(final float firstGradient, final float filledRegion, final Color color1, final Color color2) {
		List<Object> g = new ArrayList<Object>(5);
		g.add(firstGradient);
		g.add(filledRegion);
		g.add(new ColorUIResource(color1));
		g.add(new ColorUIResource(color2));
		g.add(new ColorUIResource(color1));
		return g;
	}
	public static Insets createInsets(final int size) {
		return new Insets(size, size, size, size);
	}
	public static void createScreenshot(final Component component, final String format, final String path) throws IOException {
		BufferedImage image = createCompatibleImage(component.getWidth(), component.getHeight(), true);
		Graphics2D g = image.createGraphics();
		component.paint(g);
		g.dispose();
		ImageIO.write(image, format, new File(path));
	}
	public static void focus(final Component component) {
		if (component instanceof Focusable)
			Focusable.class.cast(component).focus();
		else
			component.requestFocusInWindow();
	}
	public static double getAutoScale(final Image image, final Dimension maxSize) {
		return getAutoScale(new Dimension(image.getWidth(null), image.getHeight(null)), maxSize);
	}
	public static double getAutoScale(final Dimension imageSize, final Dimension maxSize) {
		double result;
		if (imageSize.width > maxSize.width)
			result = (double)maxSize.width / (double)imageSize.width;
		else
			result = 1.0d;
		int height = (int)((double)imageSize.height * result);
		if (height > maxSize.height)
			result *= (double)maxSize.height / (double)height;
		return result;
	}
	public static Color getBrighter(final Color color) {
		return getBrighter(color, 25);
	}
	public static Color getBrighter(final Color color, final int percent) {
		final float FACTOR_1 = (100 - (float)percent) / 100;
		final float FACTOR_2 = ((float)percent / 100) * 255;
		int R = (int)((color.getRed() * FACTOR_1) + FACTOR_2);
		int G = (int)((color.getGreen() * FACTOR_1) + FACTOR_2);
		int B = (int)((color.getBlue() * FACTOR_1) + FACTOR_2);
		return new Color(R, G, B);
	}
	public static Color getBrighter(final Color color, final Color safeColor) {
		return getBrighter(color, 25, safeColor);
	}
	public static Color getBrighter(final Color color, final int percent, final Color safeColor) {
		return (color == null) ? safeColor : getBrighter(color, percent);
	}
	@SuppressWarnings("unchecked")
	public static <T> T getClientProperty(final JComponent component, final String key, final T defaultValue) {
		Object value = component.getClientProperty(key);
		return (value == null) ? defaultValue : (T)value;
	}
	public static Color getColor(final String key) {
		return UIManager.getColor(key);
	}
	public static Color getColor(final Color color, final float factor) {
		if (factor == 0.0f)
			return color;
		int R = color.getRed();
		int G = color.getGreen();
		int B = color.getBlue();
		R = Math.min(Math.max((int)(R * factor), 0), 255);
		G = Math.min(Math.max((int)(G * factor), 0), 255);
		B = Math.min(Math.max((int)(B * factor), 0), 255);
		return new Color(R, G, B);
	}
	public static Color getColor(final Color color, final float factor, final Color safeColor) {
		return (color == null) ? safeColor : getColor(color, factor);
	}
	public static Color getDarker(final Color color) {
		return getColor(color, 0.94f);
	}
	public static Color getDarker(final Color color, final Color safeColor) {
		return (color == null) ? safeColor : getColor(color, 0.94f);
	}
	public static int getDefaultFontSize() {
		return 12;
	}
	public static LookAndFeelType getLookAndFeelType() {
		synchronized (UI.class) {
			if (lookAndFeelType == null) {
				LookAndFeel laf = UIManager.getLookAndFeel();
				String className = (laf == null) ? null : laf.getClass().getName();
				if ("com.sun.java.swing.plaf.gtk.GTKLookAndFeel".equals(className))
					lookAndFeelType = LookAndFeelType.GTK;
				else if (
					"com.sun.java.swing.plaf.windows.WindowsLookAndFeel".equals(className) ||
					"com.sun.java.swing.plaf.windows.WindowsClassicLookAndFeel".equals(className)
				)
					lookAndFeelType = LookAndFeelType.WINDOWS;
				else if ("org.jvnet.substance.SubstanceLookAndFeel".equals(className))
					lookAndFeelType = LookAndFeelType.SUBSTANCE;
				else if ("sun.swing.plaf.nimbus.NimbusLookAndFeel".equals(className))
					lookAndFeelType = LookAndFeelType.NIMBUS;
				else if (
					(laf instanceof MetalLookAndFeel) &&
					(MetalLookAndFeel.getCurrentTheme() instanceof OceanTheme)
				)
					lookAndFeelType = LookAndFeelType.METAL;
				else
					lookAndFeelType = LookAndFeelType.UNKNOWN;
			}
		}
		return lookAndFeelType;
	}
	public static Dimension getScreenSize() {
		return Toolkit.getDefaultToolkit().getScreenSize();
	}
	public static ButtonUI getSimpleButtonUI() {
		synchronized (UI.class) {
			if (simpleButtonUI == null)
