	public static ButtonGroup group(final AbstractButton... buttons) {
		ButtonGroup buttonGroup = new ButtonGroup();
		for (AbstractButton i : buttons) {
			if (i != null)
				buttonGroup.add(i);
		}
		return buttonGroup;
	}
	public static void invokeAndWait(final Runnable r) throws InterruptedException, InvocationTargetException {
		if (SwingUtilities.isEventDispatchThread())
			r.run();
		else
			SwingUtilities.invokeAndWait(r);
	}
	public static void invokeLater(final Runnable r) {
		if (SwingUtilities.isEventDispatchThread())
			r.run();
		else
			SwingUtilities.invokeLater(r);
	}
	public static boolean isAdvanced() {
		return isGTK() || isSubstance() || isNimbus() || isWindows();
	}
	public static boolean isNimbus() {
		return getLookAndFeelType() == LookAndFeelType.NIMBUS;
	}
	public static void paintFocus(final Component c, final Graphics2D g, final int insets) {
		int w = c.getWidth();
		int h = c.getHeight();
		Color color = c.getForeground();
		if (color == null)
			color = Color.BLACK;
		g.setColor(deriveAlpha(color, 127));
		Stroke oldStroke = g.getStroke();
		g.setStroke(new BasicStroke(1, BasicStroke.CAP_SQUARE, BasicStroke.JOIN_ROUND));
		g.drawRect(insets, insets, w - insets - 2, h - insets - 2);
		g.setStroke(oldStroke);
	}
	public static void replaceComponent(final Container container, final Component remove, final Component add, final Object constraints) {
		if (remove == add)
			return;
		boolean shouldValidate = false;
		if (remove != null) {
			container.remove(remove);
			shouldValidate = true;
		}
		if (add != null) {
			if (constraints == null) {
				if ((container instanceof MPanel) && (add instanceof JComponent))
					MPanel.class.cast(container).add((JComponent)add); 				else
					container.add(add);
			}
			else {
				container.add(add, constraints);
			}
			shouldValidate = true;
		}
		if (shouldValidate) {
			if (container instanceof JComponent) {
				JComponent jc = (JComponent)container;
				jc.revalidate();
			}
			else {
				container.validate();
			}
			container.repaint();
		}
	}
	public static void setStyle(final String style, final Component... components) {
		if (TK.isEmpty(style))
			return;
		try {
						Style s = new Style(components[0].getFont(), style);
			for (Component i : components)
				s.apply(i);
					}
		catch (ParseException exception) {
			MLogger.exception(exception);
		}
	}
	public static int getTypeForImageFormat(String format) {
		if (format == null)
			return BufferedImage.TYPE_INT_ARGB;
		format = format.toUpperCase();
		if (format.equals("BMP"))
			return BufferedImage.TYPE_3BYTE_BGR;
		if (format.equals("JPG"))
			return BufferedImage.TYPE_INT_RGB;
		return BufferedImage.TYPE_INT_ARGB;
	}
	public static Color getXorColor(final Color color) {
		if (color == null)
			return null;
		return new Color(color.getRGB() ^ 0xffffff);
