				border = BorderUIResource.getBlackLineBorderUIResource();
			}
			defaults.put("ToolTip.background", bg);
			defaults.put("ToolTip.backgroundInactive", bg);
			defaults.put("ToolTip.foreground", fg);
			defaults.put("ToolTip.foregroundInactive", fg);
			defaults.put("ToolTip.border", border);
			defaults.put("ToolTip.borderInactive", border);
						defaults.put("Tree.collapsedIcon", MIcon.small("ui/plus"));
			defaults.put("Tree.expandedIcon", MIcon.small("ui/minus"));
			defaults.put("Tree.leafIcon", null);
		}
						int size =
			isWindows()
			? 3
			: MTabbedPane.BORDER_INSETS_SIZE;
		defaults.put("TabbedPane.contentBorderInsets", createInsets(size));
