	}
	public static boolean isPopupTrigger(final KeyEvent e) {
		if (TK.isKeyStroke(e, VK_CONTEXT_MENU) || TK.isKeyStroke(e, VK_F10, SHIFT_MASK)) {
			e.consume();
			return true;
		}
		return false;
	}
	public static boolean isSubstance() {
