	}
	public static boolean isWindows() {
		return (getLookAndFeelType() == LookAndFeelType.WINDOWS);
	}
	public static String makeHTML(final String text, final int flags) {
		String code =
			((flags & ESCAPE) != 0)
			? TK.escapeXML(text)
			: text;
		return HTML_BEGIN + code + HTML_END;
	}
	public static String makeHTML(final String text) {
		return makeHTML(text, 0);
	}
	public static void paintRoundedBackground(final JComponent c, final Graphics graphics) {
		if (c.isBackgroundSet())
			paintRoundedBackground(c, graphics, c.getBackground(), 9, createInsets(0));
	}
	public static void paintRoundedBackground(
		final JComponent c,
		final Graphics graphics,
		final Color color,
		final int arc,
		final Insets insets
	) {
		Graphics2D g = (Graphics2D)graphics;
		g.setColor(color);
		Shape shape = new RoundRectangle2D.Float(
			insets.left,
			insets.top,
			c.getWidth() - (insets.left + insets.right),
			c.getHeight() - (insets.top + insets.bottom),
			arc,
			arc
		);
		Object saveAA = setAntialiasing(g, true);
		g.fill(shape);
		g.setRenderingHint(RenderingHints.KEY_ANTIALIASING, saveAA);
	}
	public static void paintText(
		final int flags,
		final Graphics2D g,
		final Rectangle r,
		final Color background,
		final Color foreground,
		final String... lines
	) {
		if (TK.isEmpty(lines))
			return;
		Flags f = new Flags(flags);
		FontMetrics metrics = g.getFontMetrics();
		int textHeight = metrics.getHeight();
		if (background != null) {
			g.setColor(background);
			int h = textHeight * lines.length;
			g.fillRect(r.x, r.y, r.width, h + metrics.getDescent());
		}
		Shape oldClip = g.getClip();
		int y = r.y;
		g.setClip(r);
		g.setColor(foreground);
		for (String i : lines) {
			y += textHeight;
			if (f.isSet(PAINT_TEXT_TRIM_LINES))
				g.drawString(i.trim(), r.x + 4, y);
			else
				g.drawString(i, r.x + 4, y);
			if (y > r.x + r.height)
				break; 		}
		g.setClip(oldClip);
	}
	public static BufferedImage createCompatibleImage(final int w, final int h, final boolean alpha) {
		GraphicsEnvironment environment = GraphicsEnvironment.getLocalGraphicsEnvironment();
		GraphicsDevice device = environment.getDefaultScreenDevice();
		GraphicsConfiguration configuration = device.getDefaultConfiguration();
		if (alpha)
			return configuration.createCompatibleImage(w, h, Transparency.TRANSLUCENT);
		return configuration.createCompatibleImage(w, h);
	}
	public static Color deriveAlpha(final Color color, final int alpha) {
		return new Color(color.getRed(), color.getGreen(), color.getBlue(), alpha);
	}
	public static Image scaleImage(final Image image, final int targetWidth, final int targetHeight, final Quality quality) {
		int w = image.getWidth(null);
		int h = image.getHeight(null);
		if ((w == targetWidth) && (h == targetHeight))
			return image;
		boolean alpha = true;
		switch (quality) {
			case LOW:
				return doScale(image, alpha, targetWidth, targetHeight, RenderingHints.VALUE_INTERPOLATION_NEAREST_NEIGHBOR);
			case MEDIUM:
				return doScale(image, alpha, targetWidth, targetHeight, RenderingHints.VALUE_INTERPOLATION_BILINEAR);
			case HIGH:
				if ((targetWidth < w) || (targetHeight < h)) {
					float fw = w;
					float fh = h;
					BufferedImage result = null;
					do {
						if (fw > targetWidth) {
							fw /= 1.5f;
							if (fw < targetWidth)
								fw = targetWidth;
						}
						if (fh > targetHeight) {
							fh /= 1.5f;
							if (fh < targetHeight)
								fh = targetHeight;
						}
						w = (int)fw;
						h = (int)fh;
						result = doScale(
							(result != null) ? result : image,
							alpha, w, h, RenderingHints.VALUE_INTERPOLATION_BILINEAR
						);
					} while ((w != targetWidth) || (h != targetHeight));
					return result;
				}
				else {
					return doScale(image, alpha, targetWidth, targetHeight, RenderingHints.VALUE_INTERPOLATION_BICUBIC);
				}
		}
		return null; 	}
	public static Object setAntialiasing(final Graphics2D g, final boolean value) {
		Key key = RenderingHints.KEY_ANTIALIASING;
		Object oldRenderingHint = g.getRenderingHint(key);
		g.setRenderingHint(key, value ? RenderingHints.VALUE_ANTIALIAS_ON : RenderingHints.VALUE_ANTIALIAS_OFF);
		return oldRenderingHint;
	}
	public static boolean setDefaultLookAndFeel() {
		return setDefaultLookAndFeel(new OceanTheme());
	}
	public static boolean setDefaultLookAndFeel(final MetalTheme theme) {
		try {
			MetalLookAndFeel metal = new MetalLookAndFeel();
			MetalLookAndFeel.setCurrentTheme(theme);
			setLookAndFeel(metal);
			return true;
		}
		catch (Exception exception) {
			MLogger.exception(exception);
			return false;
		}
	}
	public static void setHTMLHelp(final JComponent component, final String text) {
		component.setToolTipText((text == null) ? null : makeHTML(text));
	}
	public static void setLookAndFeel(final LookAndFeel laf) throws Exception {
		synchronized (UI.class) {
			lookAndFeelType = null;
		}
		MLogger.info("core", "Setting look and feel: %s", laf.getName());
		UIManager.setLookAndFeel(laf);
		init();
	}
	public static void setLookAndFeel(final String className) throws Exception {
		synchronized (UI.class) {
			lookAndFeelType = null;
		}
		MLogger.info("core", "Setting look and feel by class name: %s", className);
		UIManager.setLookAndFeel(className);
		init();
	}
	public static Object setTextAntialiasing(final Graphics2D g, final boolean value) {
		Key key = RenderingHints.KEY_TEXT_ANTIALIASING;
		Object oldRenderingHint = g.getRenderingHint(key);
		g.setRenderingHint(key, value ? RenderingHints.VALUE_TEXT_ANTIALIAS_ON : RenderingHints.VALUE_TEXT_ANTIALIAS_OFF);
		return oldRenderingHint;
	}
	public static void setWaitCursor(final Component component, final boolean yes) {
		component.setCursor(Cursor.getPredefinedCursor(yes ? Cursor.WAIT_CURSOR : Cursor.DEFAULT_CURSOR));
	}
	public static void setWaitCursor(final boolean yes) {
		setWaitCursor(MFrame.getGlobal(), yes);
	}
	public static void setWatermarkEnabled(final JComponent c, final boolean yes) {
				if (isSubstance())
			c.putClientProperty("substancelaf.watermark.toignore", yes ? null : true);
	}
	public static void strikeThrough(final Component component, final Graphics graphics, final String text) {
		strikeThrough(component, graphics, deriveAlpha(Color.RED, 127), text);
	}
	public static void strikeThrough(final Component component, final Graphics graphics, final Color color, final String text) {
		if (TK.isEmpty(text))
			return;
		Graphics2D g = (Graphics2D)graphics;
		g.setColor(color);
		int y1 = component.getHeight() / 2;
		FontMetrics metrics = g.getFontMetrics(component.getFont());
		int x2 = Math.min(component.getWidth() - 5, metrics.stringWidth(text));
		g.drawLine(5, y1, x2, y1);
	}
	public static BufferedImage toBufferedImage(final Icon source) {
		BufferedImage result = createCompatibleImage(source.getIconWidth(), source.getIconHeight(), true);
		Graphics2D g = result.createGraphics();
		source.paintIcon(null, g, 0, 0);
		g.dispose();
		return result;
	}
	public static BufferedImage toBufferedImage(final Image source, final boolean useSource) {
		if (useSource && (source instanceof BufferedImage))
			return (BufferedImage)source;
				BufferedImage result = createCompatibleImage(source.getWidth(null), source.getHeight(null), true);
		Graphics2D g = result.createGraphics();
		g.drawImage(source, 0, 0, null, null);
		g.dispose();
		return result;
	}
	@Uninstantiable private UI() { }
	private static BufferedImage doScale(final Image image, final boolean alpha, final int w, final int h, final Object hint) {
		BufferedImage result = createCompatibleImage(w, h, alpha);
		Graphics2D g = result.createGraphics();
		g.setRenderingHint(RenderingHints.KEY_INTERPOLATION, hint);
				g.drawImage(image, 0, 0, w, h, null);
		g.dispose();
		return result;
	}
