		protected void paintFocus(
			final Graphics g,
			final AbstractButton b,
			final Rectangle viewRect,
			final Rectangle textRect,
			final Rectangle iconRect
		) {
			UI.paintFocus(b, (Graphics2D)g, 1);
		}
