	public static void paintText(
		final int flags,
		final Graphics2D g,
		final Rectangle r,
		final Color background,
		final Color foreground,
		final String... lines
	) {
		if (TK.isEmpty(lines))
			return;
		Flags f = new Flags(flags);
		FontMetrics metrics = g.getFontMetrics();
		int textHeight = metrics.getHeight();
		if (background != null) {
			g.setColor(background);
			int h = textHeight * lines.length;
			g.fillRect(r.x, r.y, r.width, h + metrics.getDescent());
		}
		Shape oldClip = g.getClip();
		int y = r.y;
		g.setClip(r);
		g.setColor(foreground);
		for (String i : lines) {
			y += textHeight;
			if (f.isSet(PAINT_TEXT_TRIM_LINES))
				g.drawString(i.trim(), r.x + 4, y);
			else
				g.drawString(i, r.x + 4, y);
			if (y > r.x + r.height)
				break; 		}
		g.setClip(oldClip);
	}
