		if (UI.buttonIcons.get())
			button.setIcon(icon);
		button.setText(text);
		mnemonic.update(button);
	}
	public void changeButton(final AbstractButton button, final String text, final String iconName) {
		changeButton(button, text, MIcon.stock(iconName));
	}
	public boolean exec() {
		return exec(null);
	}
	public boolean exec(final JComponent defaultFocus) {
		MMenu.hideCurrentPopup();
		MSplashScreen.close();
		Container parent = getParent();
		setLocationRelativeTo(parent);
				if (defaultFocus != null) {
			addWindowListener(new WindowAdapter() {
				@Override
				public void windowActivated(final WindowEvent e) {
					defaultFocus.requestFocusInWindow();
					MDialog.this.removeWindowListener(this);
				}
			} );
		}
				Dimension screenSize = UI.getScreenSize();
		Dimension currentSize = getSize();
		Dimension newSize = new Dimension(
			Math.min(screenSize.width, currentSize.width),
			Math.min(screenSize.height, currentSize.height)
		);
		if (!newSize.equals(currentSize)) {
			setSize(newSize);
			setLocationRelativeTo(parent);
		}
		if (isModal() && (parent instanceof MDialog)) {
			MDialog parentDialog = MDialog.class.cast(parent);
						Component oldParentFocus = parentDialog.getFocusOwner();
						BooleanProperty isCancelEnabled = disableButton(parentDialog.getCancelButton());
			BooleanProperty isOKEnabled = disableButton(parentDialog.getOKButton());
			BooleanProperty isURLEnabled = disableButton(parentDialog.getURLButton());
			BooleanProperty isUserEnabled = disableButton(parentDialog.getUserButton());
						try {
				setVisible(true);
			}
			finally {
								enableButton(parentDialog.getCancelButton(), isCancelEnabled);
				enableButton(parentDialog.getOKButton(), isOKEnabled);
				enableButton(parentDialog.getURLButton(), isURLEnabled);
				enableButton(parentDialog.getUserButton(), isUserEnabled);
								if (oldParentFocus != null)
					oldParentFocus.requestFocusInWindow();
			}
		}
		else {
			setVisible(true);
		}
		return code;
	}
	public MPanel getButtonsPanel() { return buttonsPanel; }
	public MButton getCancelButton() { return cancelButton; }
	public MButton getOKButton() { return okButton; }
	public MURLButton getURLButton() { return urlButton; }
	public MButton getUserButton() { return userButton; }
	public void packFixed() {
		pack();
		setResizable(false);
	}
	public void packFixed(final int width) {
		Dimension size = new Dimension(width, getHeight());
		setMaximumSize(size);
		setMinimumSize(size);
		packFixed();
	}
	public final void reject() {
		done(false);
	}
	public void setDefault(final JButton b) {
		b.setDefaultCapable(true);
		getRootPane().setDefaultButton(b);
	}
	public void setToolBar(final MToolBar toolBar) {
		toolBar.setOpaque(false);
		if (toolBar.getOrientation() == MToolBar.HORIZONTAL)
			addNorth(toolBar);
		else 			addEast(toolBar);
	}
	public void addCenter(final JComponent component) {
		UI.addCenter(getMainPanel(), component);
	}
	public void addEast(final JComponent component) {
		getMainPanel().add(component, BorderLayout.LINE_END);
	}
	public void addNorth(final JComponent component) {
		getMainPanel().add(component, BorderLayout.PAGE_START);
	}
	public void addSouth(final JComponent component) {
		getMainPanel().add(component, BorderLayout.PAGE_END);
	}
	public void addWest(final JComponent component) {
		getMainPanel().add(component, BorderLayout.LINE_START);
	}
	protected void applyMnemonic(final Container c) {
		mnemonic.apply(c);
	}
	protected void bind(final int condition, final Action action) {
		MAction.connect(contentPane, condition, action);
	}
	protected void bind(final int condition, final int keyCode, final Action action) {
		MAction.connect(contentPane, condition, keyCode, action);
	}
	protected boolean done(final boolean value) {
		code = value;
				if (code) {
			if (!onAccept())
				return false; 		}
				else {
			if ((cancelButton != null) && !cancelButton.isEnabled())
				return false;
			if (!onReject())
				return false; 		}
		onClose();
		dispose();
		return true;
	}
	protected void fireStateChanged(final String id) {
		if (eventList == null)
			return;
		StateEvent e = null;
		for (StateListener i : eventList.getListeners(StateListener.class)) {
			if (e == null)
				e = new StateEvent(this, id);
			i.stateChanged(e);
		}
	}
	protected MPanel getMainPanel() {
		if (mainPanel == null) {
			mainPanel = MPanel.createBorderPanel(5);
			mainPanel.setOpaque(false);
			add(mainPanel, BorderLayout.CENTER);
		}
		return mainPanel;
	}
	protected boolean onAccept() { return true; }
	protected void onClose() { }
	protected boolean onReject() { return true; }
	protected void onURLClick() {
		if (urlButton != null)
			urlButton.openURL();
	}
	protected void onUserClick() { }
	protected void setCancelButton(final MButton value) { cancelButton = value; }
	protected void setOKButton(final MButton value) { okButton = value; }
	private BooleanProperty disableButton(final MButton button) {
		BooleanProperty state =
			(button == null)
			? null
			: new BooleanProperty(button.isEnabled());
		if ((state != null) && state.get())
			button.setEnabled(false);
		return state;
	}
	private void doCancel() {
		if ((cancelButton != null) && cancelButton.isEnabled() && cancelButton.isVisible())
			cancelButton.doClick();
		else
			reject();
	}
	private boolean doClick(final MButton b) {
		if ((b != null) && b.hasFocus() && b.isEnabled() && b.isVisible()) {
			b.doClick();
			return true;
		}
		return false;
	}
	private void enableButton(final MButton button, final BooleanProperty state) {
		if ((state != null) && state.get())
			button.setEnabled(true);
	}
	private void init(final String title, final Icon icon, final int f) {
		flags.setAll(f);
		if (flags.isSet(CLOSE_BUTTON))
			flags.add(OK_BUTTON);
		setModal(flags.isSet(MODAL));
		Dimension defaultSize = new Dimension(64, 64);
		setMinimumSize(defaultSize);
		setSize(defaultSize);
		setTitle(title);
				contentPane = MPanel.createBorderPanel();
		contentPane.setMargin(CONTENT_MARGIN);
		setBackground(contentPane.getBackground());
		setContentPane(contentPane);
		if (flags.isSet(OK_BUTTON) || flags.isSet(CANCEL_BUTTON)) {
			buttonsPanel =
				flags.isSet(SIDE_BUTTONS)
				? MPanel.createVBoxPanel()
				: MPanel.createHBoxPanel();
			buttonsPanel.setOpaque(false);
						if (flags.isSet(USER_BUTTON)) {
				userButton = new MButton() {
					@Override
					protected void onClick() {
						MDialog.this.onUserClick();
					}
				};
				buttonsPanel.add(userButton);
			}
						if (flags.isSet(URL_BUTTON)) {
				urlButton = new MURLButton() {
					@Override
					protected void onClick() {
						MDialog.this.onURLClick();
					}
				};
				if (flags.isSet(USER_BUTTON))
					buttonsPanel.addGap(CONTENT_MARGIN);
				buttonsPanel.add(urlButton);
			}
						if (flags.isSet(OK_BUTTON)) {
				okButton = new MButton() {
					@Override
					protected void onClick() {
						MDialog.this.accept();
					}
				};
				if (flags.isSet(CLOSE_BUTTON)) {
					if (UI.buttonIcons.get())
						okButton.setIcon("ui/close");
					okButton.setText(mnemonic.apply(okButton, _("&Close")));
				}
				else {
					if (UI.buttonIcons.get())
						okButton.setIcon("ui/ok");
					okButton.setText(mnemonic.apply(okButton, _("&OK")));
				}
			}
			if (flags.isSet(CANCEL_BUTTON)) {
				cancelButton = new MButton() {
					@Override
					protected void onClick() {
						MDialog.this.reject();
					}
				};
				if (UI.buttonIcons.get())
					cancelButton.setIcon("ui/cancel");
				cancelButton.setText(mnemonic.apply(cancelButton, _("&Cancel")));
			}
						int okCancelPanelFlags;
			if (flags.isSet(SIDE_BUTTONS))
				okCancelPanelFlags = MButtonPanel.VERTICAL;
			else
				okCancelPanelFlags = MButtonPanel.HORIZONTAL;
			MButtonPanel okCancelPanel = new MButtonPanel(
				okCancelPanelFlags,
				okButton,
				cancelButton
			);
			MPanel bottomPanel = MPanel.createBorderPanel(CONTENT_MARGIN);
			if (flags.isSet(SIDE_BUTTONS))
				bottomPanel.setMargin(0, CONTENT_MARGIN, 0, 0); 			else
				bottomPanel.setMargin(CONTENT_MARGIN, 0, 0, 0); 			bottomPanel.setOpaque(false);
			if (flags.isSet(SIDE_BUTTONS)) {
				bottomPanel.addNorth(buttonsPanel);
				bottomPanel.addSouth(okCancelPanel);
			}
			else {
				bottomPanel.addWest(buttonsPanel);
				bottomPanel.addEast(okCancelPanel);
			}
			add(
				bottomPanel,
				flags.isSet(SIDE_BUTTONS)
				? BorderLayout.LINE_END 				: BorderLayout.PAGE_END 			);
						if (flags.isSet(OK_BUTTON))
				setDefault(okButton);
		}
		bind(
			JComponent.WHEN_ANCESTOR_OF_FOCUSED_COMPONENT,
			VK_ENTER,
			new MAction("MDialog.accept") {
				@Override
				public void onAction() {
					if (doClick(cancelButton))
						return;
					if (doClick(okButton))
						return;
					if (doClick(userButton))
						return;
					if (doClick(urlButton))
						return;
					if (okButton == null)
						accept();
					else
						okButton.doClick();
				}
			}
		);
		bind(
			JComponent.WHEN_ANCESTOR_OF_FOCUSED_COMPONENT,
			VK_ESCAPE,
			new MAction("MDialog.reject") {
				@Override
				public void onAction() {
					doCancel();
				}
			}
		);
		addComponentListener(new ComponentAdapter() {
			@Override
			public void componentShown(final ComponentEvent e) {
				applyMnemonic(MDialog.this);
			}
		} );
		addWindowListener(new WindowAdapter() {
			@Override
			public void windowClosing(final WindowEvent e) {
				doCancel();
			}
		} );
				if (flags.isClear(NO_SPECIAL_EFFECTS) && UI.specialEffects.get()) {
			UI.setStyle("border-style: outset", getRootPane());
			setUndecorated(true);
			if (!TK.isEmpty(title)) {
				coolTitle = new CoolLabel(title, icon, true);
				String style = "cursor: pointer";
				if (icon == null)
					style += "; margin-bottom: " + CONTENT_MARGIN;
				coolTitle.setStyle(style);
				add(coolTitle, BorderLayout.PAGE_START); 
								coolTitle.addMouseListener(new MMouseAdapter() {
					@Override
					public void mousePressed(final MouseEvent e) {
						coolTitle.setStyle("cursor: move");
						startDrag = SwingUtilities.convertPoint(coolTitle, e.getPoint(), MDialog.this);
					}
					@Override
					public void mouseReleased(final MouseEvent e) {
						coolTitle.setStyle("cursor: pointer");
						startDrag.setLocation(0, 0);
					}
				} );
				coolTitle.addMouseMotionListener(new MouseMotionAdapter() {
					@Override
					public void mouseDragged(final MouseEvent e) {
						setLocationByPlatform(false);
						Point p = e.getLocationOnScreen();
						p.translate(-startDrag.x, -startDrag.y);
						setLocation(p);
						e.consume();
					}
				} );
			}
		}
	}
}"
